INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1369943, 'Bosentan', 'Lurasidone', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of lurasidone, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'The potential for diminished pharmacologic effects of lurasidone should be considered during coadministration with CYP450 3A4 inducers. Alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of lurasidone should be considered during coadministration with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1369944, 'Bosentan', 'Lurbinectedin', 'Major', 'Coadministration with a strong or moderate CYP450 3A4 inducer is predicted to decrease lurbinectedin systemic exposure, which may reduce its efficacy.', 'DDInter', 1767317475, 'Concomitant use of lurbinectedin and strong or moderate CYP450 3A4 inducers should generally be avoided.', 'Metabolism', 'Concomitant use of lurbinectedin and strong or moderate CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1369945, 'Bosentan', 'Macimorelin', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of macimorelin, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'The prescribing information for macimorelin recommends discontinuing potent CYP450 3A4 inducers (e.g., carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, rifamycins, St. John''s wort) as well as some moderate ones (e.g., bosentan, efavirenz, etravirine, modafinil) prior to macimorelin administration. A sufficient washout period following discontinuation of the inducers is also advised before using macimorelin. No recommendations are available for other, less potent CYP450 3A4 inducers; however, it may be appropriate to follow the same precaution.', 'Metabolism', 'The prescribing information for macimorelin recommends discontinuing potent CYP450 3A4 inducers (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
