INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1369947, 'Bosentan', 'Maraviroc', 'Moderate', 'Coadministration with inducers of the CYP450 3A4 isoenzyme and/or P-glycoprotein efflux transporter may decrease the plasma concentrations of maraviroc, which is a substrate of both.', 'DDInter', 1767317475, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, caution is advised if maraviroc is used with CYP450 3A4 and/or P-glycoprotein inducers. A dosage adjustment for maraviroc may be necessary if an interaction is suspected.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, caution is advised if maraviroc is used with CYP450 3A4 and/or P-glycoprotein inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1369948, 'Bosentan', 'Medroxyprogesterone acetate', 'Major', 'RECOMMENDED: Coadministration with bosentan may decrease the plasma concentrations and efficacy of contraceptive hormones. The mechanism is bosentan induction of CYP450 3A4, the isoenzyme primarily responsible for the metabolic clearance of sex hormones.', 'DDInter', 1767317475, 'Hormonal contraceptives, including oral, injectable, transdermal, and implantable forms, may not be reliable during concomitant therapy with bosentan. Because use of bosentan is likely associated with major birth defects, it is particularly important that patients not become pregnant during treatment. Therefore, hormonal contraceptives should not be used as the sole method of birth control in women of childbearing potential treated with bosentan. Input from a gynecologist or similar expert on adequate contraception, including emergency contraception, should be sought as needed. Intrauterine systems are unlikely to be significantly affected because of their local action.', 'Metabolism', 'Hormonal contraceptives, including oral, injectable, transdermal, and implantable forms, may not be reliable during concomitant therapy with bosentan.', NULL, NULL, 'Major', 'pharmacodynamic');
