INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1370014, 'Bosentan', 'Quazepam', 'Moderate', 'Coadministration with bosentan may decrease the plasma concentrations of drugs that are substrates of the CYP450 2C9 and/or 3A4 isoenzymes. The mechanism is accelerated clearance due to induction of those isoenzymes by bosentan.', 'DDInter', 1767317475, 'When drugs that are known substrates of CYP450 2C9 and/or 3A4 are coadministered with bosentan, the possibility of a diminished therapeutic response to those drugs should be considered. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs, particularly those with a narrow therapeutic range, whenever bosentan is added to or withdrawn from therapy.', 'Metabolism', 'When drugs that are known substrates of CYP450 2C9 and/or 3A4 are coadministered with bosentan, the possibility of a diminished therapeutic response to those drugs should be considered.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1370015, 'Bosentan', 'Quetiapine', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of quetiapine, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Pharmacologic response to quetiapine should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the quetiapine dosage adjusted as necessary. Patients should be advised to notify their physician if their symptoms worsen or their condition changes.', 'Metabolism', 'Pharmacologic response to quetiapine should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the quetiapine dosage adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
