INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1370018, 'Bosentan', 'Quinine', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of quinine, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Caution is advised if quinine is used in combination with CYP450 3A4 inducers such as carbamazepine, phenobarbital, phenytoin, and St. John''s wort. The possibility of diminished therapeutic efficacy should be considered.', 'Metabolism', 'Caution is advised if quinine is used in combination with CYP450 3A4 inducers such as carbamazepine, phenobarbital, phenytoin, and St.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1370019, 'Bosentan', 'Ramelteon', 'Moderate', 'Coadministration with inducers of CYP450 isoenzymes may decrease the plasma concentrations and pharmacologic effects of ramelteon, which is metabolized by CYP450 1A2 and, to a lesser extent, by CYP450 3A4 and the 2C subfamily of isoenzymes.', 'DDInter', 1767317475, 'The efficacy of ramelteon may be reduced when prescribed with potent inducers of CYP450 isoenzymes such as carbamazepine, enzalutamide, phenobarbital, phenytoin, rifampin, and St. John''s wort. Other known inducers include aminoglutethimide, bexarotene, bosentan, dabrafenib, dexamethasone, efavirenz, etravirine, mitotane, modafinil, nafcillin, nevirapine, rifabutin, rifapentine, barbiturates and various other anticonvulsants, although the extent to which they interact with ramelteon is unknown.', 'Metabolism', 'The efficacy of ramelteon may be reduced when prescribed with potent inducers of CYP450 isoenzymes such as carbamazepine, enzalutamide, phenobarbital, phenytoin, rifampin, and St.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1370020, 'Bosentan', 'Ranolazine', 'Major', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of ranolazine, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'The manufacturer considers the use of ranolazine to be contraindicated in patients taking potent CYP450 3A4 inducers such as carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St. John''s wort. The extent to which other, less potent CYP450 3A4 inducers may interact with ranolazine is unknown. Caution is advised if they are used with ranolazine.', 'Metabolism', 'The manufacturer considers the use of ranolazine to be contraindicated in patients taking potent CYP450 3A4 inducers such as carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1370021, 'Bosentan', 'Relugolix', 'Moderate', 'Coadministration with dual inducers of CYP450 3A4 and P-glycoprotein (P-gp) may decrease the plasma concentrations of relugolix. In vitro, relugolix is metabolized primarily by CYP450 3A and, to a lesser extent, by CYP450 2C8. Relugolix is also a substrate for intestinal P-gp.', 'DDInter', 1767317475, 'The potential for diminished pharmacologic effects of relugolix should be considered during coadministration with dual CYP450 3A4 and P-gp inducers. Alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of relugolix should be considered during coadministration with dual CYP450 3A4 and P-gp inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1370022, 'Bosentan', 'Remdesivir', 'Moderate', 'Concomitant use of remdesivir with other agents that are known to induce hepatotoxicity may theoretically increase the risk of liver injury. Data from investigational use and clinical studies suggest that remdesivir may be associated with hepatotoxic effects.', 'DDInter', 1767317475, 'Until more information is available, concomitant use of remdesivir with known hepatotoxic drugs should be avoided when possible. Liver function should be evaluated prior to starting remdesivir and monitored during treatment as clinically appropriate. Remdesivir should not be initiated in patients with ALT greater than or equal to 5 times the upper limit of normal (ULN) at baseline. Likewise, remdesivir should be discontinued immediately if ALT increases to 5 times ULN or greater during treatment (may be restarted when ALT falls below 5 times ULN), or if ALT elevation is accompanied by signs or symptoms of liver inflammation or increasing conjugated bilirubin, alkaline phosphatase, or INR.', 'Synergism', 'Until more information is available, concomitant use of remdesivir with known hepatotoxic drugs should be avoided when possible.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1370023, 'Bosentan', 'Repaglinide', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of repaglinide, which is metabolized by the isoenzyme in the intestine and liver.', 'DDInter', 1767317475, 'Because the antidiabetic effect of repaglinide is dose- and concentration-dependent, patients treated concomitantly with rifampin may require a higher dosage of repaglinide. Blood glucose should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy in patients stabilized on their existing antidiabetic regimen, and the repaglinide dosage adjusted as necessary. Patients should be advised to contact their physician if they experience loss of glycemic control.', 'Metabolism', 'Because the antidiabetic effect of repaglinide is dose- and concentration-dependent, patients treated concomitantly with rifampin may require a higher dosage of repaglinide.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1370024, 'Bosentan', 'Ribociclib', 'Moderate', 'Coadministration of bosentan with a drug that is both a substrate as well as inhibitor of CYP450 2C9 and/or 3A4 may result in increased plasma concentrations of bosentan and decreased plasma concentrations of the other drug. Bosentan itself is a substrate and inducer of both CYP450 2C9 and 3A4. Theoretically, bosentan may induce metabolism of the coadministered drug while its own metabolism may be inhibited by the coadministered drug.', 'DDInter', 1767317475, 'When a drug that is both a substrate as well as inhibitor of CYP450 2C9 and/or 3A4 is coadministered with bosentan, the possibility of diminished therapeutic response to the coadministered drug should be considered. Clinical and/or laboratory monitoring may be appropriate whenever bosentan is added to or withdrawn from therapy, and the dosage of the concomitant drug adjusted as necessary. The possibility of prolonged and/or increased pharmacologic effects of bosentan, including serious adverse effects such as hepatotoxicity, should also be considered. Patients should be advised to notify their physician if they experience signs and symptoms of hepatotoxicity such as fever, rash, anorexia, nausea, vomiting, fatigue, right upper quadrant pain, dark urine, and jaundice. Concomitant administration of bosentan with both a potent CYP450 2C9 inhibitor (e.g., fluconazole, amiodarone) and a potent CYP450 3A4 inhibitor (e.g., ketoconazole, itraconazole, ritonavir) is not recommended. Concomitant administration with combination CYP450 2C9/3A4 inhibitors (e.g., delavirdine, imatinib, miconazole, mifepristone, voriconazole) should probably be avoided also, if possible.', 'Metabolism', 'When a drug that is both a substrate as well as inhibitor of CYP450 2C9 and/or 3A4 is coadministered with bosentan, the possibility of diminished therapeutic response to the coadministered drug should be considered.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1370025, 'Bosentan', 'Rifabutin', 'Moderate', 'Coadministration with bosentan may decrease the plasma concentrations of drugs that are substrates of the CYP450 2C9 and/or 3A4 isoenzymes. The mechanism is accelerated clearance due to induction of those isoenzymes by bosentan.', 'DDInter', 1767317475, 'When drugs that are known substrates of CYP450 2C9 and/or 3A4 are coadministered with bosentan, the possibility of a diminished therapeutic response to those drugs should be considered. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs, particularly those with a narrow therapeutic range, whenever bosentan is added to or withdrawn from therapy.', 'Metabolism', 'When drugs that are known substrates of CYP450 2C9 and/or 3A4 are coadministered with bosentan, the possibility of a diminished therapeutic response to those drugs should be considered.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1370026, 'Bosentan', 'Rifapentine', 'Moderate', 'Coadministration with rifapentine may decrease the plasma concentrations of drugs that are substrates of the CYP450 2C8, 2C9, and/or 3A4 isoenzymes. The mechanism is accelerated clearance due to induction of these isoenzymes by rifapentine. Enzyme activities may be induced within 4 days of the first dose and return to normal 14 days after discontinuation of rifapentine.', 'DDInter', 1767317475, 'The possibility of a diminished therapeutic response to drugs that are known substrates of CYP450 2C8, 2C9, and/or 3A4 should be considered during coadministration with rifapentine. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs, particularly those with a narrow therapeutic range, whenever rifapentine is added to or withdrawn from therapy.', 'Metabolism', 'The possibility of a diminished therapeutic response to drugs that are known substrates of CYP450 2C8, 2C9, and/or 3A4 should be considered during coadministration with rifapentine.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1370027, 'Bosentan', 'Rilpivirine', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of rilpivirine, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'To minimize the risk of reduced viral susceptibility and resistance development associated with subtherapeutic levels of rilpivirine, caution and clinical monitoring may be advisable if CYP450 3A4 inducers are coadministered with rilpivirine.', 'Metabolism', 'To minimize the risk of reduced viral susceptibility and resistance development associated with subtherapeutic levels of rilpivirine, caution and clinical monitoring may be advisable if CYP450 3A4 inducers are coadministered with rilpivirine.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1370028, 'Bosentan', 'Rimegepant', 'Major', 'Coadministration with strong or moderate inducers of CYP450 3A4 may significantly reduce the plasma concentrations and therapeutic effects of rimegepant, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Coadministration of rimegepant with strong or moderate inducers of CYP450 3A4 should be avoided.', 'Metabolism', 'Coadministration of rimegepant with strong or moderate inducers of CYP450 3A4 should be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1370029, 'Bosentan', 'Riociguat', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of riociguat, which is partially metabolized by the isoenzyme.', 'DDInter', 1767317475, 'The potential for diminished therapeutic effects of riociguat should be considered when prescribed in combination with CYP450 3A4 inducers. Patients should be closely monitored, and the dosage of riociguat adjusted as necessary. Data are not available to guide dosing of riociguat when coadministered with potent CYP450 3A4 inducers such as carbamazepine, enzalutamide, phenobarbital, phenytoin, rifamycins, and St. John''s wort. Alternative agents with no or minimal CYP450 3A4-inducing activity should be considered whenever possible.', 'Metabolism', 'The potential for diminished therapeutic effects of riociguat should be considered when prescribed in combination with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1370030, 'Bosentan', 'Ripretinib', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations and anti-tumor activities of ripretinib and its active metabolite (DP-5439). The proposed mechanism is induction of CYP450 3A4, the isoenzyme responsible for the metabolic clearance of ripretinib and DP-5439.', 'DDInter', 1767317475, 'Until more information is available, caution is advised when ripretinib is used with a CYP450 3A4 inducer. Clinical and laboratory monitoring should be considered whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the ripretinib dosage adjusted as necessary. Patients should be monitored for loss of anti-tumor activity.', 'Metabolism', 'Until more information is available, caution is advised when ripretinib is used with a CYP450 3A4 inducer.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1370031, 'Bosentan', 'Ritonavir', 'Major', 'Coadministration with the pharmacokinetic boosters cobicistat and ritonavir may significantly increase the plasma concentrations of bosentan, particularly during the first few days of combined use. The proposed mechanism is inhibition of the OATP-mediated uptake of bosentan into hepatocytes.', 'DDInter', 1767317475, 'The dosage of bosentan should be adjusted when used in combination with cobicistat or ritonavir. In patients who have been receiving cobicistat or ritonavir for at least 10 days when bosentan is prescribed, the latter should be initiated at 62.5 mg once daily or every other day depending on individual tolerability. Conversely, in patients who have been receiving bosentan when cobicistat or ritonavir is prescribed, the manufacturers recommend that use of bosentan be discontinued for at least 36 hours prior to initiating the pharmacokinetic booster. After at least 10 days following the initiation of cobicistat or ritonavir, bosentan may be resumed at 62.5 mg once daily or every other day based upon individual tolerability. There is limited experience with abrupt discontinuation of bosentan. Although no evidence for acute rebound has been observed, gradual dose reduction (62.5 mg twice daily for 3 to 7 days) should be considered to minimize the potential for clinical deterioration.', 'Others', 'The dosage of bosentan should be adjusted when used in combination with cobicistat or ritonavir.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1370032, 'Bosentan', 'Rivaroxaban', 'Moderate', 'Coadministration with inducers of CYP450 3A4 and/or P-glycoprotein (P-gp) may decrease the plasma concentrations of rivaroxaban, which is a substrate of the isoenzyme as well as the efflux transporter.', 'DDInter', 1767317475, 'Caution is advised if rivaroxaban is used in combination with inducers of CYP450 3A4 and/or P-glycoprotein. Pharmacologic effects of rivaroxaban should be monitored more closely whenever the inducer is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised if rivaroxaban is used in combination with inducers of CYP450 3A4 and/or P-glycoprotein.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1370033, 'Bosentan', 'Rolapitant', 'Moderate', 'Coadministration with moderate inducers of CYP450 3A4 may decrease the plasma concentrations of rolapitant, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'The potential for diminished pharmacologic effects of rolapitant should be considered during coadministration with CYP450 3A4 inducers. Alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of rolapitant should be considered during coadministration with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1370034, 'Bosentan', 'Rucaparib', 'Moderate', 'Coadministration with inhibitors of CYP450 2C9 and/or 3A4 may increase the plasma concentrations of bosentan, which is metabolized by these isoenzymes. Concomitant administration of both a CYP450 2C9 inhibitor and a CYP450 3A4 inhibitor may lead to even larger increases in plasma concentrations of bosentan.', 'DDInter', 1767317475, 'The possibility of prolonged and/or increased pharmacologic effects of bosentan, including serious adverse effects such as hepatotoxicity, should also be considered during coadministration with CYP450 2C9 or 3A4 inhibitors. Patients should be advised to notify their physician if they experience signs and symptoms of hepatotoxicity such as fever, rash, anorexia, nausea, vomiting, fatigue, right upper quadrant pain, dark urine, and jaundice. Concomitant administration of bosentan with both a potent CYP450 2C9 inhibitor (e.g., fluconazole, amiodarone) and a potent CYP450 3A4 inhibitor (e.g., ketoconazole, itraconazole, ritonavir) is not recommended. Concomitant administration with dual inhibitors of CYP450 2C9 and 3A4 (e.g., delavirdine, imatinib, miconazole, voriconazole) should probably be avoided also, if possible.', 'Metabolism', 'The possibility of prolonged and/or increased pharmacologic effects of bosentan, including serious adverse effects such as hepatotoxicity, should also be considered during coadministration with CYP450 2C9 or 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1370035, 'Bosentan', 'Rufinamide', 'Moderate', 'Rufinamide may modestly or moderately decrease the plasma concentrations and therapeutic effects of drugs that are CYP450 3A4 substrates. The mechanism is enhanced metabolism by CYP450 3A4 enzyme induction.', 'DDInter', 1767317475, 'During concomitant treatment with rufinamide and drugs that are CYP450 3A4 substrates, it is recommended that patients should be monitored for 2 weeks when rufinamide is added to or withdrawn from therapy, or after increases of the dose. Dose adjustments of the coadministered drug may be required if an interaction is suspected.', 'Metabolism', 'During concomitant treatment with rufinamide and drugs that are CYP450 3A4 substrates, it is recommended that patients should be monitored for 2 weeks when rufinamide is added to or withdrawn from therapy, or after increases of the dose.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1370036, 'Bosentan', 'Ruxolitinib', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of ruxolitinib, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'No dosage adjustment is recommended when ruxolitinib is coadministered with a CYP450 3A4 inducer. However, patients should be closely monitored and the dose titrated based on safety and efficacy.', 'Metabolism', 'No dosage adjustment is recommended when ruxolitinib is coadministered with a CYP450 3A4 inducer.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1370037, 'Bosentan', 'Sacituzumab govitecan', 'Major', 'Coadministration with inducers of uridine diphosphate glucuronosyltransferase 1A1 (UGT1A1) may significantly decrease the plasma concentrations of sacituzumab govitecan and its pharmacologically active metabolite, SN-38. The proposed mechanism involves decreased exposure to SN-38, a topoisomerase I inhibitor linked to sacituzumab govitecan, which is metabolized via UGT1A1. Loss of antineoplastic efficacy may occur.', 'DDInter', 1767317475, 'Due to the potential loss of antitumour activity, concomitant use of sacituzumab govitecan with UGT1A1 inducers should be avoided.', 'Metabolism', 'Due to the potential loss of antitumour activity, concomitant use of sacituzumab govitecan with UGT1A1 inducers should be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1370038, 'Bosentan', 'Saquinavir', 'Moderate', 'Coadministration of a protease inhibitor (PI) in combination with bosentan may increase the plasma concentration of bosentan and decrease that of the PI, unless ritonavir is included as a pharmacokinetic booster. Both PIs and bosentan are primarily metabolized by CYP450 3A4. Because PIs are also inhibitors of CYP450 3A4, they can inhibit the metabolic clearance of bosentan. Conversely, bosentan may induce the metabolism of PIs, although the effects are probably not significant in the presence of ritonavir, which is a potent CYP450 3A4 inhibitor.', 'DDInter', 1767317475, 'Protease inhibitors should generally not be used in combination with bosentan unless ritonavir is also added as a pharmacokinetic booster. Dose adjustment of bosentan has been recommended. In patients who are starting bosentan and have been receiving PI therapy for at least 10 days, it has been recommended to start bosentan at 62.5 mg once daily or every other day based upon individual tolerability. When starting PI therapy in patients already on bosentan, it has been recommended to discontinue bosentan for at least 36 hours prior to initiation of PI therapy. After at least 10 days following the initiation of PI therapy, bosentan may be resumed at 62.5 mg once daily or every other day based upon individual tolerability. According to some authorities, use of the fixed combination atazanavir-cobicistat with bosentan is not recommended.', 'Metabolism', 'Protease inhibitors should generally not be used in combination with bosentan unless ritonavir is also added as a pharmacokinetic booster.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1370039, 'Bosentan', 'Saxagliptin', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of saxagliptin, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Dosage adjustment of saxagliptin is not necessary during coadministration with rifampin or other CYP450 3A4 inducers. However, glycemic control should be closely monitored, and the physician notified if an interaction is suspected.', 'Metabolism', 'Dosage adjustment of saxagliptin is not necessary during coadministration with rifampin or other CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1370040, 'Bosentan', 'Selpercatinib', 'Major', 'Coadministration with potent or moderate inducers of CYP450 3A4 may significantly decrease the plasma concentrations of selpercatinib, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Concomitant use of selpercatinib with potent or moderate CYP450 3A4 inducers should generally be avoided.', 'Metabolism', 'Concomitant use of selpercatinib with potent or moderate CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1370041, 'Bosentan', 'Selumetinib', 'Major', 'Coadministration with potent or moderate inducers of CYP450 3A4 may decrease the plasma concentrations of selumetinib, which is primarily metabolized by CYP450 3A4 and to a lesser extent by CYP450 2C19, 1A2, 2C9, 2E1, and 3A5. Selumetinib also undergoes glucuronidation by UGT1A1 and UGT1A3.', 'DDInter', 1767317475, 'Concomitant use of selumetinib with potent or moderate CYP450 3A4 inducers should generally be avoided.', 'Metabolism', 'Concomitant use of selumetinib with potent or moderate CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1370042, 'Bosentan', 'Sibutramine', 'Moderate', 'Coadministration with bosentan may decrease the plasma concentrations of drugs that are substrates of the CYP450 2C9 and/or 3A4 isoenzymes. The mechanism is accelerated clearance due to induction of those isoenzymes by bosentan.', 'DDInter', 1767317475, 'When drugs that are known substrates of CYP450 2C9 and/or 3A4 are coadministered with bosentan, the possibility of a diminished therapeutic response to those drugs should be considered. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs, particularly those with a narrow therapeutic range, whenever bosentan is added to or withdrawn from therapy.', 'Metabolism', 'When drugs that are known substrates of CYP450 2C9 and/or 3A4 are coadministered with bosentan, the possibility of a diminished therapeutic response to those drugs should be considered.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1370043, 'Bosentan', 'Sildenafil', 'Moderate', 'Coadministration of bosentan and sildenafil may result in increased bosentan plasma concentrations and decreased sildenafil plasma concentrations. The mechanism is sildenafil inhibition of bosentan metabolism via CYP450 3A4 and 2C9, and bosentan induction of sildenafil metabolism via these same isoenzymes.', 'DDInter', 1767317475, 'No dosage adjustment of either drug is necessary, whether sildenafil is used for the treatment of pulmonary arterial hypertension (PAH) or erectile dysfunction. However, adding sildenafil to bosentan therapy does not provide any beneficial effect on exercise capacity in the treatment of PAH. If prescribed together, the possibility of prolonged and/or increased pharmacologic effects of bosentan, including serious adverse effects such as hepatotoxicity, should be considered. Patients should be advised to seek medical attention if they experience signs and symptoms of hepatotoxicity such as fever, rash, anorexia, nausea, vomiting, fatigue, right upper quadrant pain, dark urine, and jaundice.', 'Metabolism', 'No dosage adjustment of either drug is necessary, whether sildenafil is used for the treatment of pulmonary arterial hypertension (PAH) or erectile dysfunction.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1370044, 'Bosentan', 'Silodosin', 'Moderate', 'Coadministration with inducers of CYP450 3A4 and/or P-glycoprotein (P-gp) may decrease the plasma concentrations of silodosin, which is a substrate of both the isoenzyme and efflux transporter.', 'DDInter', 1767317475, 'The potential for diminished pharmacologic effects of silodosin should be considered during coadministration with CYP450 3A4 and/or P-gp inducers. Alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of silodosin should be considered during coadministration with CYP450 3A4 and/or P-gp inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1370045, 'Bosentan', 'Simvastatin', 'Moderate', 'Coadministration with bosentan may decrease the plasma concentrations of simvastatin and other HMG-CoA reductase inhibitors that are metabolized by the CYP450 3A4 isoenzyme. The mechanism is accelerated clearance due to induction of the isoenzyme by bosentan.', 'DDInter', 1767317475, 'The possibility of diminished HMG-CoA reductase inhibitory activity should be considered during concomitant use of atorvastatin, cerivastatin, lovastatin, simvastatin, or red yeast rice (which contains lovastatin) with bosentan. Cholesterol levels should be checked following initiation or discontinuation of bosentan therapy, and the dosage of HMG-CoA reductase inhibitor adjusted accordingly. Pravastatin and fluvastatin may be reasonable alternatives, since they are metabolized by other hepatic enzymes.', 'Metabolism', 'The possibility of diminished HMG-CoA reductase inhibitory activity should be considered during concomitant use of atorvastatin, cerivastatin, lovastatin, simvastatin, or red yeast rice (which contains lovastatin) with bosentan.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1370046, 'Bosentan', 'Siponimod', 'Major', 'Coadministration with drugs that cause moderate induction of CYP450 2C9 and strong induction of CYP450 3A4 may decrease the plasma concentrations of siponimod, which is primarily metabolized by these isoenzymes. This interaction includes concomitant use of siponimod with a moderate CYP450 2C9/strong CYP450 3A4 dual inducer or a moderate CYP450 2C9 inducer in combination with a separate strong CYP450 3A4 inducer.', 'DDInter', 1767317475, 'Concomitant use of siponimod and drugs that cause moderate induction of CYP450 2C9 and strong induction of CYP450 3A4 (e.g., carbamazepine, enzalutamide, rifampin) is not recommended for all patients. Concomitant use of siponimod and moderate or strong CYP450 3A4 inducers (e.g., apalutamide, bosentan, dabrafenib, dexamethasone, efavirenz, eslicarbazepine, etravirine, fosphenytoin, lorlatinib, lumacaftor, mitotane, modafinil, nafcillin, nevirapine, phenobarbital, phenytoin, primidone, rifabutin, rifapentine, St. John''s wort) is not recommended for patients with CYP450 2C9*1/*3 and *2/*3 genotypes.', 'Metabolism', 'Concomitant use of siponimod and drugs that cause moderate induction of CYP450 2C9 and strong induction of CYP450 3A4 (e.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1370047, 'Bosentan', 'Sirolimus', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of sirolimus and tacrolimus, both of which are primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Given the risk of organ rejection associated with inadequate immunosuppressant drug levels, caution is advised during concomitant therapy with CYP450 3A4 inducers. Sirolimus and tacrolimus blood levels should be checked frequently and the dosage adjusted accordingly whenever a CYP450 3A4 inducer is added to or withdrawn from therapy.', 'Metabolism', 'Given the risk of organ rejection associated with inadequate immunosuppressant drug levels, caution is advised during concomitant therapy with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1370048, 'Bosentan', 'Nitrous acid', 'Moderate', 'Coadministration with antihypertensive agents, diuretics, vasodilators, or phosphodiesterase-5 (PDE5) inhibitors may potentiate the hypotensive effect of sodium nitrite. Since sodium nitrite can cause serious hypotension and methemoglobin formation, patients may be at increased risk for complications related to inadequate perfusion and oxygenation.', 'DDInter', 1767317475, 'Sodium nitrite should be used with caution in the presence of concomitant antihypertensive agents, diuretics, vasodilators, or PDE5 inhibitors. Hemodynamics should be monitored during and after administration of sodium nitrite, and the infusion rate decreased if significant hypotension occurs. In addition, methemoglobin levels should be monitored and oxygen administered during treatment whenever possible.', 'Synergism', 'Sodium nitrite should be used with caution in the presence of concomitant antihypertensive agents, diuretics, vasodilators, or PDE5 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1370049, 'Bosentan', 'Solifenacin', 'Moderate', 'Theoretically, coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of solifenacin, which has been shown to be a substrate of the isoenzyme in vitro.', 'DDInter', 1767317475, 'Pharmacologic response to solifenacin should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the solifenacin dosage adjusted if necessary.', 'Metabolism', 'Pharmacologic response to solifenacin should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the solifenacin dosage adjusted if necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1370050, 'Bosentan', 'Somapacitan', 'Moderate', 'Coadministration with human growth hormone may decrease the plasma concentrations of drugs that are metabolized by CYP450 3A4.', 'DDInter', 1767317475, 'Caution is advised when human growth hormone is used concurrently with drugs that are known CYP450 3A4 substrates, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever human growth hormone is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when human growth hormone is used concurrently with drugs that are known CYP450 3A4 substrates, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
