INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1370055, 'Bosentan', 'Stiripentol', 'Moderate', 'Coadministration with inducers of CYP450 1A2, 2C19, and/or 3A4 may decrease the plasma concentrations of stiripentol, which has been shown in vitro to undergo phase I metabolism via these pathways.', 'DDInter', 1767317475, 'The potential for diminished pharmacologic effects of stiripentol should be considered during coadministration with CYP450 inducers. Alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of stiripentol should be considered during coadministration with CYP450 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1370056, 'Bosentan', 'Sunitinib', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of sunitinib and its pharmacologically active metabolite, both of which are substrates of the isoenzyme.', 'DDInter', 1767317475, 'The potential for diminished pharmacologic effects of sunitinib should be considered during coadministration with CYP450 3A4 inducers. Alternative treatments or dose increase for sunitinib may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of sunitinib should be considered during coadministration with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1370057, 'Bosentan', 'Suvorexant', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of suvorexant, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'The potential for diminished therapeutic effects of suvorexant should be considered when prescribed with inducers of CYP450 3A4. Pharmacologic effects of suvorexant should be monitored more closely whenever an inducer is added to or withdrawn from therapy, and the suvorexant dosage adjusted as necessary.', 'Metabolism', 'The potential for diminished therapeutic effects of suvorexant should be considered when prescribed with inducers of CYP450 3A4.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1370058, 'Bosentan', 'Tacrolimus', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of sirolimus and tacrolimus, both of which are primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Given the risk of organ rejection associated with inadequate immunosuppressant drug levels, caution is advised during concomitant therapy with CYP450 3A4 inducers. Sirolimus and tacrolimus blood levels should be checked frequently and the dosage adjusted accordingly whenever a CYP450 3A4 inducer is added to or withdrawn from therapy.', 'Metabolism', 'Given the risk of organ rejection associated with inadequate immunosuppressant drug levels, caution is advised during concomitant therapy with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1370059, 'Bosentan', 'Tadalafil', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of tadalafil, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'For the treatment of benign prostatic hyperplasia and erectile dysfunction, no dosage adjustment of tadalafil is warranted during coadministration with CYP450 3A4 inducers. However, the possibility of diminished pharmacologic effects of tadalafil should be considered. For the treatment of pulmonary arterial hypertension, use of tadalafil should be avoided in patients chronically taking potent inducers of CYP450 3A4.', 'Metabolism', 'For the treatment of benign prostatic hyperplasia and erectile dysfunction, no dosage adjustment of tadalafil is warranted during coadministration with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1370060, 'Bosentan', 'Tamoxifen', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of tamoxifen, which is metabolized by the isoenzyme.', 'DDInter', 1767317475, 'In general, agents without CYP450 3A4-inducing activity are preferable in patients treated with tamoxifen, particularly if these agents are to be used for a prolonged period. Otherwise, pharmacologic response to tamoxifen should be monitored closely whenever an inducing agent is added to or withdrawn from therapy, and the tamoxifen dosage adjusted if necessary.', 'Metabolism', 'In general, agents without CYP450 3A4-inducing activity are preferable in patients treated with tamoxifen, particularly if these agents are to be used for a prolonged period.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1370061, 'Bosentan', 'Tasimelteon', 'Moderate', 'Coadministration with inducers of CYP450 1A2 and/or 3A4 may decrease the plasma concentrations of tasimelteon, which is primarily metabolized by these isoenzymes.', 'DDInter', 1767317475, 'The potential for diminished therapeutic effects of tasimelteon should be considered during coadministration with inducers of CYP450 1A2 and/or 3A4. Alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished therapeutic effects of tasimelteon should be considered during coadministration with inducers of CYP450 1A2 and/or 3A4.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1370062, 'Bosentan', 'Tazemetostat', 'Major', 'Coadministration with moderate or potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of tazemetostat, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Concomitant use of tazemetostat with moderate or potent CYP450 3A4 inducers should generally be avoided. Alternative therapeutic agents with less enzyme induction potential should be considered whenever possible.', 'Metabolism', 'Concomitant use of tazemetostat with moderate or potent CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1370063, 'Bosentan', 'Telaprevir', 'Moderate', 'Coadministration of bosentan with boceprevir or telaprevir is expected to increase the plasma concentrations of bosentan and decrease the plasma concentrations of the hepatitis C virus (HCV) NS3/4A protease inhibitor. The proposed mechanism involves inhibition of bosentan metabolism via CYP450 3A4 and, conversely, induction of boceprevir and telaprevir metabolism via the same isoenzyme. The interaction has not been specifically studied with either boceprevir or telaprevir, but has been reported with HIV protease inhibitors.', 'DDInter', 1767317475, 'Caution is recommended if bosentan is used in combination with boceprevir or telaprevir. The possibility of prolonged and/or increased pharmacologic effects of bosentan, including serious adverse effects such as hepatotoxicity, should also be considered. Patients should be advised to notify their physician if they experience signs and symptoms of hepatotoxicity such as fever, rash, anorexia, nausea, vomiting, fatigue, right upper quadrant pain, dark urine, and jaundice. Close clinical monitoring of the virologic response to the HCV NS3/4A protease inhibitor is also appropriate.', 'Metabolism', 'Caution is recommended if bosentan is used in combination with boceprevir or telaprevir.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1370064, 'Bosentan', 'Telithromycin', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of telithromycin, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'The potential for diminished therapeutic effects of telithromycin should be considered when prescribed with CYP450 3A4 inducers. Pharmacologic response to telithromycin should be monitored closely during coadministration, and alternative treatment given if an interaction is suspected. Whenever possible, telithromycin should preferably not be used during or within 2 weeks after discontinuation of treatment with a CYP450 3A4 inducer.', 'Metabolism', 'The potential for diminished therapeutic effects of telithromycin should be considered when prescribed with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1370065, 'Bosentan', 'Telotristat ethyl', 'Moderate', 'Coadministration with telotristat ethyl may decrease the plasma concentrations of drugs that are substrates of CYP450 3A4 and/or 2B6 isoenzymes. The proposed mechanism is accelerated clearance due to induction of CYP450 3A4 and/or 2B6 (in vitro) isoenzymes by telotristat ethyl.', 'DDInter', 1767317475, 'When drugs that are known substrates of CYP450 3A4 and/or 2B6 are coadministered with telotristat ethyl, the possibility of a diminished therapeutic response to those drugs should be considered. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs, particularly those with a narrow therapeutic range, whenever telotristat ethyl is added to or withdrawn from therapy.', 'Metabolism', 'When drugs that are known substrates of CYP450 3A4 and/or 2B6 are coadministered with telotristat ethyl, the possibility of a diminished therapeutic response to those drugs should be considered.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1370066, 'Bosentan', 'Temsirolimus', 'Moderate', 'Coadministration of temsirolimus with inducers of CYP450 3A4 may decrease the plasma concentrations of sirolimus, a major active metabolite of temsirolimus and known substrate of CYP450 3A4.', 'DDInter', 1767317475, 'In the setting of mantle cell lymphoma, concomitant use of temsirolimus with inducers of CYP450 3A4 should generally be avoided. When used for the treatment of renal cell carcinoma, some authorities advise a dose increase from the recommended 25 mg IV once weekly to 50 mg IV once weekly depending on patient tolerability. The dosage should be reduced to the normally recommended dose following discontinuation of the potent CYP450 3A4 inducer.', 'Metabolism', 'In the setting of mantle cell lymphoma, concomitant use of temsirolimus with inducers of CYP450 3A4 should generally be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
