INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (77598, 'Dabrafenib', 'Lesinurad', 'Moderate', 'Coadministration with dabrafenib may decrease the plasma concentrations of drugs that are primarily metabolized by CYP450 3A4 and/or 2C9. Dabrafenib has been found in vitro to be an inducer of these isoenzymes.', NULL, 'Caution is advised when dabrafenib is prescribed with drugs that undergo metabolism by CYP450 3A4 and/or 2C9.', NULL, 'Caution is advised when dabrafenib is prescribed with drugs that undergo metabolism by CYP450 3A4 and/or 2C9. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever dabrafenib is added to or withdrawn from therapy. Significantly reduced plasma concentrations and loss of efficacy may occur with sensitive substrates of CYP450 3A4 or 2C9 such as hormonal contraceptives, immunosuppressants (cyclosporine, everolimus, sirolimus, tacrolimus), ivacaftor, and warfarin-type anticoagulants. Alternatives to these medications should be considered if possible.', 'Metabolism', 'Dacomitinib, Pexidartinib, Fedratinib, Bosutinib, Vandetanib, Cobimetinib, Ibrutinib, Afatinib, Tivozanib, Regorafenib, Ruxolitinib, More', 'Pegloticase, Febuxostat, Colchicine, Sulfinpyrazone, Rasburicase, Probenecid, Allopurinol', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/77699/', '[1] "Product Information. Tafinlar (dabrafenib)." GlaxoSmithKline, Research Triangle Park, NC.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Ambien (zolpidem)." sanofi-aventis, Bridgewater, NJ.[4] Yamreudeewong W, Henann NE, Fazio A, Lower DL, Cassidy TG "Drug-food interactions in clinical practice." J Fam Pract 40 (1995): 376-84[5] "Product Information. Tafinlar (dabrafenib)." GlaxoSmithKline, Research Triangle Park, NC.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (77599, 'Dabrafenib', 'Letermovir', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 and/or 2C8 may increase the plasma concentrations of dabrafenib and its active metabolites. Coadministration with dabrafenib may decrease the plasma concentrations of drugs that are substrates of CYP450 3A4, including many of the known inhibitors of the isoenzyme such as conivaptan, delavirdine, nefazodone, telithromycin, and most azole antifungal agents, macrolide antibiotics, and protease inhibitors.', NULL, 'The use of dabrafenib with potent CYP450 2C8 inhibitors such as gemfibrozil or potent CYP450 3A4 inhibitors such as ceritinib, clarithromycin, cobicistat, conivaptan, delavirdine, erythromycin, idelalisib, nefazodone, telithromycin, and most protease inhibitors and azole antifungal agents should generally be avoided if possible.', NULL, 'The use of dabrafenib with potent CYP450 2C8 inhibitors such as gemfibrozil or potent CYP450 3A4 inhibitors such as ceritinib, clarithromycin, cobicistat, conivaptan, delavirdine, erythromycin, idelalisib, nefazodone, telithromycin, and most protease inhibitors and azole antifungal agents should generally be avoided if possible. Some authorities recommend avoiding concomitant use of dabrafenib during and for 2 weeks after treatment with itraconazole. Otherwise, patients should be closely monitored for development of adverse effects such as febrile reactions (high fever or fever accompanied by rigors, hypotension, dehydration, or renal failure), hyperglycemia, uveitis, and cutaneous malignancies (e.g., squamous cell carcinoma, keratoacanthoma, melanoma).', 'Metabolism', 'Sunitinib, Futibatinib, Regorafenib, Tucatinib, Vem