INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (77979, 'Dacomitinib', 'Sacituzumab govitecan', 'Major', 'Coadministration of sacituzumab govitecan with inhibitors of uridine diphosphate glucuronosyltransferase 1A1 (UGT1A1) may increase the risk of adverse effects, such as nausea, vomiting, diarrhea, and neutropenia. The proposed mechanism involves increased exposure to the pharmacologically active metabolite SN-38, a topoisomerase I inhibitor linked to sacituzumab govitecan, which is metabolized via UGT1A1. Genetic variants of the UGT1A1 gene such as the UGT1A1*28 allele lead to reduced UGT1A1 enzyme activity and patients who are homozygous for the UGT1A1*28 allele are at increased risk for neutropenia from SN-38.', NULL, 'Concomitant use of sacituzumab govitecan with UGT1A1 inhibitors should be avoided.', NULL, 'Concomitant use of sacituzumab govitecan with UGT1A1 inhibitors should be avoided. If coadministration is unavoidable, patients should be closely monitored for the development of severe nausea, vomiting, diarrhea, and neutropenia. Patients should be advised to promptly report symptoms such as chills, fever, malaise, sore throat, mouth sores, unusual fatigue, bruising, or bleeding.', 'Synergism', 'Entrectinib, Imatinib, Pralsetinib, Dabrafenib, Asciminib, Acalabrutinib, Pexidartinib, Pacritinib, Tepotinib, Fedratinib, Avapritinib, More', 'Pertuzumab, Cetuximab, Avelumab, Panitumumab, Obinutuzumab, Bevacizumab, Necitumumab, Dostarlimab, Ramucirumab, Atezolizumab, Amivantamab, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/78080/', '[1] "Product Information. Trodelvy (sacituzumab govitecan)." Immunomedics, Morris Plains, NJ.[2] "Product Information