INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (77995, 'Dactinomycin', 'Chloramphenicol (ophthalmic)', 'Moderate', 'Coadministration of chloramphenicol with other agents that can cause bone marrow depression or aplastic anemia may increase the risk and/or severity of chloramphenicol hematologic toxicity. Serious and fatal blood dyscrasias (aplastic anemia, hypoplastic anemia, thrombocytopenia and granulocytopenia) have been reported after the administration of chloramphenicol.', NULL, 'Concurrent use of chloramphenicol with other bone marrow depressing agents should be avoided.', NULL, 'Concurrent use of chloramphenicol with other bone marrow depressing agents should be avoided.', 'Synergism', 'Tetracycline, Mupirocin, Rifamycin, Oxytetracycline, Bacitracin, Demeclocycline, Rifaximin, Gentamicin, Amikacin, Neomycin, Tetracycline, More', 'Ixabepilone, Valrubicin', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/78096/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Sim SM, Back DJ, Breckenridge AM "The effect of various drugs on the glucuronidation of zidovudine (azidothymidine; AZT) by human liver microsomes." Br J Clin Pharmacol 32 (1991): 17-21[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] Burger DM, Meenhorst PL, Koks CH, Beijnen JH "Drug interactions with zidovudine." AIDS 7 (1993): 445-60[5] "Product Information. Chloromycetin (chloramphenicol)." Parke-Davis, Morris Plains, NJ.[6] Cerner Multum, Inc "ANVISA Bulrio Eletrnico. Available from: URL: http://www.anvisa.gov.br/datavisa/fila_bula/index.asp." O 0 (2015):[7] Agencia Espaola de Medicamentos y Productos Sanitarios Healthcare "Centro de informacin online d