INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (78117, 'Dalfampridine', 'Rucaparib', 'Moderate', 'Coadministration with rucaparib may increase the plasma concentrations and the risk of adverse effects of drugs that are substrates of Multidrug and Toxin Extrusion (MATE) 1, MATE 2-K, organic cation transporter (OCT) 1, and/or OCT 2. The proposed mechanism, based on in vitro data, is decreased clearance due to rucaparib-mediated inhibition of these transport proteins. Rucaparib appears to be a potent inhibitor of MATE 1 and MATE2-K, a moderate inhibitor of OCT1, and a weak inhibitor of OCT2.', NULL, 'Caution is advised if rucaparib is used concomitantly with drugs that are substrates of MATE 1, MATE 2-K, OCT 1 and/or OCT 2, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if rucaparib is used concomitantly with drugs that are substrates of MATE 1, MATE 2-K, OCT 1 and/or OCT 2, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever rucaparib is added to or withdrawn from therapy with these drugs. Patients should be monitored for the development of adverse effects.', 'Excretion', 'Dextromethorphan, Sodium oxybate, Inotersen, Amifampridine, Tafamidis', 'Pertuzumab, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Tazemetostat, Altretamine, Tagraxofusp, Pegaspargase, Ixazomib, Sotorasib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/78218/', '[1] EMA. European Medicines Agency. European Union "EMA - List of medicines under additional monitoring. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/regulation/document_listing/document_listing_000366.jsp&mid=WC0b01ac058067c852" ([2013 - ]):[2] "Product Information. Lonsurf (tipiracil-trifluridine)." Taiho Oncology, Inc., Princeton, NJ.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (78118, 'Dalfampridine', 'Tafenoquine', 'Moderate', 'Coadministration with tafenoquine may increase the plasma concentrations of drugs that are substrates of organic cation transporter-2 (OCT2) or multidrug and toxin extrusion (MATE) transporters. Clinical drug interaction studies with tafenoquine and OCT2 and MATE substrates have not been conducted in humans. However, in vitro observations suggest the potential for increased concentrations of these substrates, which may result in increased toxicities. Specifically, tafenoquine inhibited metformin transport via human OCT2, MATE-1, and MATE2-K transporters in vitro.', NULL, 'Concomitant use of tafenoquine with OCT2 and MATE substrates should generally be avoided.', NULL, 'Concomitant use of tafenoquine with OCT2 and MATE substrates should generally be avoided. If coadministration is required, patients should be monitored for drug-related toxicities and dosage reductions considered as needed in accordance with the product labeling of the coadministered drugs.', 'Excretion', 'Artesunate, Mefloquine, Lumefantrine, Artemether, Pyrimethamine, Atovaquone, Hydroxychloroquine, Halofantrine, Quinine, Primaquine, Proguanil, More', 'Dextromethorphan, Valbenazine, Sodium oxybate, Riluzole, Tetrabenazine, Inotersen, Pitolisant, Amifampridine, Deutetrabenazine, Tafamidis', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/78219/', '[1] "Product Information. Krintafel (tafenoquine)." GlaxoSmithKline, Research Triangle Park, NC.[2] "Product Information. Lonsurf (tipiracil-trifluridine)." Taiho Oncology, Inc., Princeton, NJ.[3] "Product Information. Krintafel (tafenoquine)." GlaxoSm