INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (78170, 'Indomethacin', 'Dalteparin', 'Major', 'In patients receiving neuraxial anesthesia or spinal puncture, the risk of developing an epidural or spinal hematoma during low molecular weight heparin (LMWH) or heparinoid therapy may be increased by the concomitant use of other drugs that affect coagulation, including nonsteroidal anti-inflammatory drugs (NSAIDs). The development of epidural and spinal hematoma can lead to long-term or permanent paralysis. Theoretically, NSAIDs may potentiate the risk of bleeding complications associated with LMWH or heparinoid therapy. NSAIDs interfere with platelet adhesion and aggregation and may prolong bleeding time in healthy individuals. While these effects are generally slight and of relatively short duration with most NSAIDs (except aspirin) at recommended dosages, they may be of pronounced clinical significance when combined with the inhibitory effects of LMWHs or heparinoids on the clotting cascade.', NULL, 'Products containing NSAIDs, especially if given chronically and in high dosages, should preferably be avoided in patients receiving LMWHs or heparinoids.', NULL, 'Products containing NSAIDs, especially if given chronically and in high dosages, should preferably be avoided in patients receiving LMWHs or heparinoids. Close clinical and laboratory observation for bleeding complications is recommended if concurrent therapy is necessary. In patients undergoing neuraxial intervention, coadministration of these agents should be approached with caution and only after thorough assessment of risks and benefits.', 'Synergism', 'Fludrocortisone, Fluocinolone acetonide, Hydrocortisone, Prednisolone, Betamethasone, Fluorometholone, Methylprednisolone, Dexamethasone, Rabeprazole, Misoprostol, Chondroitin sulfate, More', 'Protein C, Acenocoumarol, Selexipag', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/78271/', '[1] Weale AE, Warwick DJ, Durant N, Prothero D "Is there a clinical interaction between low molecular weight heparin and non-steroidal analgesics after total hip replacement?" Ann R Coll Surg Engl 77 (1995): 35-7[2] "Product Information. Orgaran (danaparoid)." Organon, West Orange, NJ.[3] "Product Information. Lovenox (enoxaparin)." Rhone-Poulenc Rorer, Collegeville, PA.[4] "Product Information. Innohep (tinzaparin)" DuPont Pharmaceuticals, Wilmington, DE.[5] "Product Information. Normiflo (ardeparin)." Wyeth-Ayerst Laboratories, Philadelphia, PA.[6] Walker AM "Predictors of bleeding during heparin therapy." JAMA 244 (1980): 1209-12[7] Klinkhardt U, Breddin HK, Esslinger HU, Haas S, Kalatzis A, Harder S "Interaction between the LMWH reviparin and aspirin in healthy volunteers." Br J Clin Pharmacol 49 (2000): 337-41[8] Heiden D, Rodvien R, Mielke CH "Heparin bleeding, platelet dysfunction, and aspirin." JAMA 246 (1981): 330-1[9] Theroux P, Ouimet H, McCans J, et al. "Aspirin, heparin, or both to treat acute unstable angina." N Engl J Med 319 (1988): 1105-6[10] Price AJ, Frcpath DO "Is there a clinical interaction between low molecular weight heparin and non-steroidal analgesics after total hip replacement?" Ann R Coll Surg Engl 77 (1995): 395[11] "Product Information. Fragmin (dalteparin)." Pharmacia and Upjohn, Kalamazoo, MI.[12] Bang CJ, Riedel B, Talstad I, Berstad A "Interaction between heparin and acetylsalicylic acid on gastric mucosal and skin bleeding in humans." Scand J Gastroenterol 27 (1992): 489-94', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (78171, 'Inotersen', 'Dalteparin', 'Major', 'Coadministration of inotersen and drugs that interfere with platelet function or coagulation may potentiate the risk of serious, potentially life-threatening bleeding complications, including spontaneous intracranial and intrapulmonary hemorrhage. Inotersen causes reductions in platelet count that may result in sudden and unpredictable thrombocytopenia.', NULL, 'Caution is advised when inotersen is prescribed with antiplatelet agents, anticoagulants, or other medications that commonly cause thrombocytopenia or bleeding.', NULL, 'Caution is advised when inotersen is prescribed with antiplatelet agents, anticoagulants, or other medications that commonly cause thrombocytopenia or bleeding. A platelet count should be obtained prior to initiation of inotersen and regularly during and for at least 8 weeks after treatment in accordance with the product labeling. Inotersen should not be administered in patients with a platelet count below 100 x 10^9/L or in patients who are unable to adhere to the recommended laboratory monitoring and management guidelines. Patients or their caregivers should be apprised of the signs and symptoms of thrombocytopenia and to seek medical attention if they occur, including any unusual or prolonged bleeding (e.g., petechiae, easy bruising, hematoma, subconjunctival bleeding, gingival bleeding, epistaxis, hemoptysis, irregular or heavier than normal menstrual bleeding, hematemesis, hematuria, hematochezia, melena), neck stiffness, or atypical severe headache.', 'Synergism', 'Protein C, Acenocoumarol, Selexipag', 'Dextromethorphan, Valbenazine, Dalfampridine, Sodium oxybate, Riluzole, Tetrabenazine, Pitolisant, Amifampridine, Deutetrabenazine, Tafamidis', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/78272/', '[1] "Product Information. Tegsedi (inotersen)." Akcea Therapeutics, Cambridge, MA.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Brukinsa (zanubrutinib)." BeiGene USA, Inc, San Mateo, CA.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (78172, 'Dalteparin', 'Tositumomab (I-131)', 'Major', 'Coadministration of tositumomab and iodine I 131 tositumomab with drugs that interfere with platelet function or coagulation may potentiate the risk of bleeding complications. Tositumomab and iodine I 131 tositumomab alone can cause severe or life-threatening thrombocytopenia.', NULL, 'Due to the frequent occurrence of severe and prolonged thrombocytopenia associated with tositumomab and iodine I 131 tositumomab, concomitant use of other medications that interfere with platelet function or coagulation should be considered cautiously.', NULL, 'Due to the frequent occurrence of severe and prolonged thrombocytopenia associated with tositumomab and iodine I 131 tositumomab, concomitant use of other medications that interfere with platelet function or coagulation should be considered cautiously. Close clinical and laboratory observation for bleeding complications is recommended during and after the therapeutic regimen.', 'Synergism', 'Protein C, Acenocoumarol, Selexipag, Desirudin', NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/78273/', '[1] "Product Information. BexxarTherapy (tositumomab)." GlaxoSmithKline Inc, Oakville, IA.[2] "Product Information. Bexxar I 131 Therapeutic (iodine I 131 tositumomab)." GlaxoSmithKline, Research Triangle Park, NC.', 1767369485);
