INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (78262, 'Dofetilide', 'Danazol', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of dofetilide, which is partially metabolized by the isoenzyme. The use of dofetilide has been associated with dose-related prolongation of the QT interval, thus elevated plasma levels of the drug may potentiate the risk of ventricular arrhythmias such as ventricular tachycardia and torsade de pointes as well as cardiac arrest and sudden death.', NULL, 'Close clinical and laboratory monitoring is recommended whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the dosage of dofetilide adjusted accordingly as necessary.', NULL, 'Close clinical and laboratory monitoring is recommended whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the dosage of dofetilide adjusted accordingly as necessary. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Metabolism', 'Quinidine, Amiodarone, Lidocaine, Propafenone, Tocainide, Procainamide, Flecainide, Disopyramide, Mexiletine, Bretylium, Moricizine, Ibutilide', 'Ulipristal, Ospemifene, Raloxifene, Bazedoxifene, Prasterone', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/78363/', '[1] "Product Information. Tikosyn (dofetilide)" Pfizer US Pharmaceuticals, Atlanta, GA.[2] "Product Information. Tikosyn (dofetilide)" Pfizer US Pharmaceuticals, Atlanta, GA.', 1767369485);
