INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (78280, 'Escitalopram', 'Danazol', 'Minor', 'Coadministration with inhibitors of CYP450 3A4 is not expected to significantly affect the pharmacokinetics of citalopram or escitalopram, both of which are partially metabolized by the isoenzyme. Administration of racemic citalopram (40 mg) in combination with the potent CYP450 3A4 inhibitor ketoconazole (200 mg) decreased the ketoconazole peak plasma concentration (Cmax) by 21% and systemic exposure (AUC) by 10%, but did not significantly affect the pharmacokinetics of citalopram.', NULL, '-', NULL, '-', 'Metabolism', 'Maprotiline, Fluoxetine, St. John''s Wort, Milnacipran, Vortioxetine, Levomilnacipran, Doxepin, Duloxetine, Isocarboxazid, Viloxazine, Amoxapine, More', 'Ulipristal, Ospemifene, Raloxifene, Bazedoxifene, Prasterone', 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/78381/', '[1] "Product Information. Lexapro (escitalopram)." Forest Pharmaceuticals, St. Louis, MO.[2] "Product Information. Celexa (citalopram)." Forest Pharmaceuticals, St. Louis, MO.[3] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[4] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[5] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[6] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (78281, 'Etravirine', 'Danazol', 'Moderate', 'Coadministration with inhibitors of CYP450 2C19, 2C9, and/or 3A4 may increase the plasma concentrations of etravirine, which is a substrate of these isoenzymes.', NULL, 'Because safety data regarding increased etravirine exposures are limited, caution is advised if etravirine is prescribed in combination with CYP450 2C19, 2C9, and/or 3A4 inhibitors.', NULL, 'Because safety data regarding increased etravirine exposures are limited, caution is advised if etravirine is prescribed in combination with CYP450 2C19, 2C9, and/or 3A4 inhibitors.', 'Metabolism', 'Ospemifene, Prasterone, Raloxifene, Bazedoxifene', 'Lamivudine, Paritaprevir, Glecaprevir, Tenofovir alafenamide, Simeprevir, Cabotegravir, Valaciclovir, Zanamivir, Lenacapavir, Telaprevir, Velpatasvir, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/78382/', '[1] "Product Information. Intelence (etravirine)." Ortho Biotech Inc, Bridgewater, NJ.[2] "Product Information. Intelence (etravirine)." Ortho Biotech Inc, Bridgewater, NJ.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (78282, 'Everolimus', 'Danazol', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 and/or P-glycoprotein may increase the blood concentrations of everolimus following oral administration. Everolimus is a substrate of both the CYP450 3A4 isoenzyme and P-glycoprotein drug efflux transporter, thus their inhibition in the intestine can enhance the absorption of everolimus.', NULL, 'The possibility of prolonged and/or increased pharmacologic effects of everolimus therapy should be considered during coadministration with CYP450 3A4 and/or P-glycoprotein inhibitors, including adverse effects such as pneumonitis, stomatitis, infection, dyspnea, diarrhea, anemia, leucopenia, thrombocytopenia, hyperglycemia, and hyperlipidemia.', NULL, 'The possibility of prolonged and/or increased pharmacologic effects of everolimus therapy should be considered during coadministration with CYP450 3A4 and/or P-glycoprotein inhibitors, including adverse effects such as pneumonitis, stomatitis, infection, dyspnea, diarrhea, anemia, leucopenia, thrombocytopenia, hyperglycemia, and hyperlipidemia. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy.', 'Absorption', 'Ospemifene, Prasterone, Raloxifene, Bazedoxifene', 'Dabrafenib, Dacomitinib, Vemurafenib, Vandetanib, Capmatinib, Afatinib, Tivozanib, Encorafenib, Sunitinib, Regorafenib, Alpelisib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/78383/', '[1] "Product Information. Afinitor (everolimus)." Novartis Pharmaceuticals, East Hanover, NJ.[2] "Product Information. Afinitor (everolimus)." Novartis Pharmaceuticals, East Hanover, NJ.[3] "Product Information. Afinitor (everolimus)." Novartis Pharmaceuticals, East Hanover, NJ.[4] "Product Information. Mavyret (glecaprevir-pibrentasvir)." Abbott Pharmaceutical, Abbott Park, IL.', 1767369485);
