INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (78315, 'Danazol', 'Ixabepilone', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of ixabepilone, which is primarily metabolized by the isoenzyme.', NULL, 'Caution is advised when ixabepilone is prescribed with CYP450 3A4 inhibitors.', NULL, 'Caution is advised when ixabepilone is prescribed with CYP450 3A4 inhibitors. Pharmacologic response to ixabepilone should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the ixabepilone dosage adjusted as necessary. Frequent monitoring of peripheral blood counts between treatment cycles is recommended, and patients should be advised to contact their physician if they experience signs and symptoms of ixabepilone toxicities such as infection or burning, tingling, or numbness in the hands and feet.', 'Metabolism', 'Ulipristal, Ospemifene, Raloxifene, Bazedoxifene, Prasterone', 'Idarubicin, Valrubicin, Plicamycin, Mitoxantrone, Bleomycin, Doxorubicin, Mitomycin, Daunorubicin, Dactinomycin', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/78416/', '[1] "Product Information. Ixempra (ixabepilone)." Bristol-Myers Squibb, Princeton, NJ.[2] "Product Information. Ixempra (ixabepilone)." Bristol-Myers Squibb, Princeton, NJ.', 1767369485);
