INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (78397, 'Danazol', 'Tacrolimus', 'Moderate', 'Limited data suggest that danazol may inhibit the CYP450 3A4 hepatic metabolism of tacrolimus. Tacrolimus serum concentrations and risk of nephrotoxicity may increase significantly.', NULL, 'Until more data are available, these agents should be used concomitantly only with caution.', NULL, 'Until more data are available, these agents should be used concomitantly only with caution. Close monitoring of tacrolimus concentrations, blood pressure, electrolytes, and kidney function is recommended. Tacrolimus dose reductions may be required. Patients should be advised to report symptoms such as nausea, vomiting, diarrhea, abdominal pain, headache, dizziness, paresthesias, or tremor.', 'Metabolism', 'Minoxidil, Deoxycholic acid, Calcium gluconate, Magnesium sulfate, Glycopyrronium, Pimecrolimus, Aminobenzoic acid, Finasteride, Ivermectin, Caffeine, Diclofenac, More', 'Ospemifene, Ulipristal, Prasterone, Bazedoxifene', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/78498/', '[1] "Product Information. Prograf (tacrolimus)." Fujisawa, Deerfield, IL.[2] Shapiro R, Venkataramanan R, Warty VS, et al "Fk 506 interaction with danazol." Lancet 341 (1993): 1344-5[3] "Product Information. Prograf (tacrolimus)." Fujisawa, Deerfield, IL.[4] Hooks MA "Tacrolimus, a new immunosuppressant--a review of the literature." Ann Pharmacother 28 (1994): 501-11', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (78398, 'Danazol', 'Tadalafil', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of tadalafil, which is primarily metabolized by the isoenzyme. The possibility of prolonged and/or increased pharmacologic effects of tadalafil should be considered.', NULL, 'Caution is advised if tadalafil is prescribed with CYP450 3A4 inhibitors.', NULL, 'Caution is advised if tadalafil is prescribed with CYP450 3A4 inhibitors. Dosage adjustments may be appropriate for tadalafil whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy based on efficacy and side effects. Tadalafil labeling recommends that the dosage not exceed 10 mg once every 72 hours in patients treated concomitantly with a potent CYP450 3A4 inhibitor, such as erythromycin, itraconazole, ketoconazole, protease inhibitors, and nefazodone. Patients should be advised to promptly notify their physician if they experience potential symptoms of PDE5 inhibitor toxicity such as pain or tightness in the chest or jaw, irregular heartbeat, nausea, shortness of breath, visual disturbances, syncope, or prolonged erection (greater than 4 hours).', 'Metabolism', 'Apomorphine, Pentosan polysulfate, Calcium chloride, Phenazopyridine, Alprostadil, Trospium, Acetohydroxamic acid, Yohimbine, Flavoxate, Avanafil, Papaverine, More', 'Ulipristal, Ospemifene, Raloxifene, Bazedoxifene, Prasterone', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/78499/', '[1] "Product Information. Cialis (tadalafil)." Lilly, Eli and Company, Indianapolis, IN.[2] "Product Information. Cialis (tadalafil)." Lilly, Eli and Company, Indianapolis, IN.[3] "Product Information. Adcirca (tadalafil)." United Therapeutics Corporation, Silver Spring, MD.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (78399, 'Danazol', 'Tazemetostat', 'Moderate', 'Coadministration with CYP450 3A4 inhibitors may increase the plasma concentrations of tazemetostat, which is primarily metabolized by the isoenzyme. Clinically, this interaction may result in an increased risk of the frequency or severity of adverse reactions due to tazemetostat such as hemorrhage, pleural effusion, skin infection, dyspnea, pain, and respiratory distress. Data are not available for other, less potent inhibitors.', NULL, 'Caution and clinical monitoring is recommended with the concomitant use of tazemetostat with CYP450 3A4 inhibitors.', NULL, 'Caution and clinical monitoring is recommended with the concomitant use of tazemetostat with CYP450 3A4 inhibitors. Patients should be advised to notify their health care professional if they experience signs or symptoms of possible tazemetostat-related adverse effects including secondary malignancies, hemorrhage, pleural effusion, skin infection, dyspnea, pain, and respiratory distress.', 'Metabolism', 'Ospemifene, Prasterone, Raloxifene, Bazedoxifene', 'Pertuzumab, Oxaliplatin, Rucaparib, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Altretamine, Tagraxofusp, Ixazomib, Sotorasib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/78500/', '[1] "Product Information. Tazverik (tazemetostat)." Epizyme, Inc, Cambridge, MA.[2] "Product Information. Tazverik (tazemetostat)." Epizyme, Inc, Cambridge, MA.[3] "Product Information. Accolate (zafirlukast)." Zeneca Pharmaceuticals, Wilmington, DE.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (78400, 'Danazol', 'Temsirolimus', 'Moderate', 'Coadministration of temsirolimus with inhibitors of CYP450 3A4 may increase the plasma concentrations of sirolimus, a major active metabolite of temsirolimus and known substrate of CYP450 3A4.', NULL, 'Caution is advised if temsirolimus is prescribed in combination with CYP450 3A4 inhibitors.', NULL, 'Caution is advised if temsirolimus is prescribed in combination with CYP450 3A4 inhibitors. Pharmacologic response to temsirolimus should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the temsirolimus dosage adjusted as necessary.', 'Metabolism', 'Ulipristal, Ospemifene, Raloxifene, Bazedoxifene, Prasterone', 'Dabrafenib, Dacomitinib, Vemurafenib, Vandetanib, Capmatinib, Afatinib, Tivozanib, Encorafenib, Sunitinib, Regorafenib, Alpelisib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/78501/', '[1] "Product Information. Torisel (temsirolimus)." Wyeth-Ayerst Laboratories, Philadelphia, PA.[2] "Product Information. Torisel (temsirolimus)." Wyeth-Ayerst Laboratories, Philadelphia, PA.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (78401, 'Danazol', 'Teriflunomide', 'Major', 'The recent, concomitant, or subsequent use (without the recommended leflunomide washout period or procedure) of other agents known to induce hepatotoxicity may potentiate the risk of liver injury associated with leflunomide. The risk is thought to extend to teriflunomide, its principal active metabolite, because recommended dosages of both result in a similar range of plasma concentrations of teriflunomide.', NULL, 'Caution is advised if leflunomide or teriflunomide must be used in patients who are currently receiving or have recently received treatment with other hepatotoxic agents (e.', NULL, 'Caution is advised if leflunomide or teriflunomide must be used in patients who are currently receiving or have recently received treatment with other hepatotoxic agents (e.g., acetaminophen; alcohol; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; interferons; ketolide and macrolide antibiotics; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice), and vice versa. Liver enzymes and bilirubin should be measured prior to initiation of leflunomide/teriflunomide therapy and at least monthly for the first six months of treatment and every 6 to 8 weeks thereafter. Patients with preexisting liver disease or elevated baseline liver enzymes (i.e., ALT greater than two times ULN) should not receive leflunomide or teriflunomide. Patients who develop elevated serum ALT greater than three times ULN while receiving these medications should discontinue treatment and be given washout procedures with cholestyramine or activated charcoal to accelerate elimination of leflunomide''s active metabolite from plasma, which otherwise may take up to two years. Follow-up monitoring should be conducted at least weekly until the ALT value is within normal range, and washout procedures repeated as necessary. All patients treated with leflunomide or teriflunomide should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', 'Ulipristal, Mifepristone, Ospemifene, Raloxifene, Bazedoxifene', 'Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, Ravulizumab, Diroximel fumarate, Mycophenolic acid, Abatacept, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/78502/', '[1] EMEA "EMEA public statement on leflunomide (ARAVA) - severe and serious hepatic reactions. Available from URL: http://www.eudra.org/emea.html." ([1999 Sept 2]):[2] "Product Information. Arava (leflunomide)." Hoechst Marion-Roussel Inc, Kansas City, MO.[3] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (78402, 'Danazol', 'Tioguanine', 'Moderate', 'The concomitant or sequential use of other agents known to induce hepatotoxicity may potentiate the risk of liver injury associated with thioguanine. A high risk of liver toxicity characterized by vascular endothelial damage has been reported with long-term continuous use of thioguanine, particularly in children receiving the drug as part of maintenance therapy for acute lymphoblastic leukemia and in other conditions associated with continuous use.', NULL, 'The risk of hepatic injury should be considered when thioguanine is used with other potentially hepatotoxic agents (e.', NULL, 'The risk of hepatic injury should be considered when thioguanine is used with other potentially hepatotoxic agents (e.g., acetaminophen; alcohol; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; interferons; ketolide and macrolide antibiotics; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice. Baseline and regular monitoring of hepatic function is recommended. Thioguanine therapy should be discontinued if there is evidence of toxic hepatitis or biliary stasis, as reversal of signs and symptoms of liver toxicity have been reported upon withdrawal. Early indications of liver toxicity are signs associated with portal hypertension such as thrombocytopenia out of proportion with neutropenia and splenomegaly. Elevations of liver enzymes have also been reported, but do not always occur.', 'Synergism', 'Fluorouracil, Pemetrexed, Mercaptopurine, Cladribine, Trifluridine, Floxuridine, Gemcitabine, Nelarabine, Cytarabine, Pralatrexate, Fludarabine, More', 'Ulipristal, Mifepristone, Ospemifene, Raloxifene, Bazedoxifene', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/78503/', '[1] "Product Information. Aubagio (teriflunomide)." Genzyme Corporation, Cambridge, MA.[2] "Product Information. Tabloid (thioguanine)." Prasco Laboratories, Cincinnati, OH.', 1767369485);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (78403, 'Danazol', 'Thyroid, porcine', 'Moderate', 'Androgens may induce reversible clinical hyperthyroidism in patients receiving thyroid hormone replacement therapy. The proposed mechanism is androgen-induced decrease in T4 binding globulin resulting in decreased serum T4, increased T3 uptake resin and free T4, and decreased TSH levels.', NULL, 'Clinical and laboratory monitoring of thyroid function may be necessary, as may a 25% to 50% reduction in thyroid hormone dosage.', NULL, 'Clinical and laboratory monitoring of thyroid function may be necessary, as may a 25% to 50% reduction in thyroid hormone dosage.', 'Synergism', 'Ulipristal, Mifepristone, Ospemifene, Raloxifene, Bazedoxifene, Prasterone', NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/78504/', '[1] Arafah BM "Decreased levothyroxine requirement in women with hypothyroidism during androgen therapy for breast cancer." Ann Intern Med 121 (1994): 247-51[2] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[3] "Product Information. Armour thyroid (thyroid desiccated)." Forest Pharmaceuticals, St. Louis, MO.[4] "Product Information. Synthroid (levothyroxine)." Abbott Pharmaceutical, Abbott Park, IL.', 1767369485);
