INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (78410, 'Danazol', 'Ubrogepant', 'Moderate', 'Coadministration with moderate or weak inhibitors of CYP450 3A4 may increase the plasma concentrations of ubrogepant, which is primarily metabolized by the isoenzyme.', NULL, 'The manufacturer recommends an initial ubrogepant dose of 50 mg when coadministered with moderate or weak CYP450 3A4 inhibitors.', NULL, 'The manufacturer recommends an initial ubrogepant dose of 50 mg when coadministered with moderate or weak CYP450 3A4 inhibitors. If needed, a second ubrogepant dose should be avoided within 24 hours of the initial dose when used with moderate CYP450 3A4 inhibitors.', 'Metabolism', 'Ospemifene, Prasterone, Raloxifene, Bazedoxifene', 'Frovatriptan, Rimegepant, Eptinezumab, Lasmiditan, Rizatriptan, Naratriptan, Galcanezumab, Zolmitriptan, Sumatriptan, Erenumab, Clonidine, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/78511/', '[1] "Product Information. Ubrelvy (ubrogepant)." Allergan Inc, Irvine, CA.[2] "Product Information. Ubrelvy (ubrogepant)." Allergan Inc, Irvine, CA.', 1767369485);
