INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (78412, 'Danazol', 'Valdecoxib', 'Moderate', 'Coadministration with inhibitors of CYP450 2C9 and/or 3A4 may increase the plasma concentrations of valdecoxib, which is metabolized by these isoenzymes.', NULL, 'The possibility of prolonged and/or increased pharmacologic effects of valdecoxib or parecoxib, including serious adverse effects such as gastrointestinal ulceration and bleeding, should be considered during concomitant therapy with CYP450 2C9 or 3A4 inhibitors, particularly combination (2C9/3A4) inhibitors such as fluconazole, fluvoxamine, imatinib, and zafirlukast.', NULL, 'The possibility of prolonged and/or increased pharmacologic effects of valdecoxib or parecoxib, including serious adverse effects such as gastrointestinal ulceration and bleeding, should be considered during concomitant therapy with CYP450 2C9 or 3A4 inhibitors, particularly combination (2C9/3A4) inhibitors such as fluconazole, fluvoxamine, imatinib, and zafirlukast. Dose reductions of the COX-2 inhibitor may be required.', 'Metabolism', 'Rabeprazole, Ketoprofen, Diclofenac, Piroxicam, Rofecoxib, Sulindac, Meclofenamic acid, Etodolac, Ketorolac, Misoprostol, Glucosamine, More', 'Ospemifene, Ulipristal, Prasterone, Bazedoxifene', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/78513/', '[1] "Product Information. Bextra (valdecoxib)." Pharmacia Corporation, Peapack, NJ.', 1767369485);
