INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (78414, 'Danazol', 'Venetoclax', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of venetoclax, which is a substrate of both the isoenzyme and efflux transporter. Increased venetoclax exposure may potentiate the risk of tumor lysis syndrome, particularly at initiation of therapy and during the dosage ramp-up phase, as well as other adverse effects such as diarrhea, nausea, vomiting, neutropenia, anemia, and thrombocytopenia.', NULL, 'Caution is advised when venetoclax is used with inhibitors of CYP450 3A4.', NULL, 'Caution is advised when venetoclax is used with inhibitors of CYP450 3A4. Close clinical and laboratory monitoring for the development of tumor lysis syndrome and myelosuppression is recommended following the addition of a CYP450 3A4, and the venetoclax dosage adjusted if necessary. The dosage may be readjusted 2 to 3 days after discontinuation of the inhibitor.', 'Metabolism', 'Ospemifene, Prasterone, Raloxifene, Bazedoxifene', 'Pertuzumab, Oxaliplatin, Rucaparib, Bortezomib, Tisagenlecleucel, Aminolevulinic acid, Omacetaxine mepesuccinate, Altretamine, Tagraxofusp, Ixazomib, Sotorasib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/78515/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Venclexta (venetoclax)." AbbVie US LLC, North Chicago, IL.[4] "Product Information. Venclexta (venetoclax)." AbbVie US LLC, North Chicago, IL.[5] "Product Information. Accolate (zafirlukast)." Zeneca Pharmaceuticals, Wilmington, DE.', 1767369485);
