    ) VALUES (1370543, 'Amprenavir', 'Brentuximab vedotin', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations of monomethyl auristatin E (MMAE), the microtubule-disrupting component of brentuximab vedotin. MMAE is primarily metabolized by CYP450 3A4.', NULL, 'Caution is advised when brentuximab is used with potent CYP450 3A4 inhibitors.', NULL, 'Caution is advised when brentuximab is used with potent CYP450 3A4 inhibitors. Close monitoring for adverse effects including neutropenia, infection, peripheral neuropathy, and hepatotoxicity is recommended.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
