INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1371172, 'Brexpiprazole', 'Berotralstat', 'Major', 'Coadministration with inhibitors of CYP450 3A4 and/or 2D6 may significantly increase the plasma concentrations of brexpiprazole, which is metabolized by these isoenzymes.', 'DDInter', 1767317475, 'The manufacturer recommends that the brexpiprazole dosage be reduced as follows during concomitant administration of CYP450 inhibitors. The brexpiprazole dosage should be increased to the original level if these agents are discontinued. It is advisable to monitor patients for clinical response.', 'Metabolism', 'The manufacturer recommends that the brexpiprazole dosage be reduced as follows during concomitant administration of CYP450 inhibitors.', NULL, NULL, 'Major', 'pharmacodynamic');
