INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (79248, 'Amoxapine', 'Darifenacin', 'Moderate', 'Coadministration with darifenacin may increase the plasma concentrations of drugs that are metabolized by CYP450 2D6, including many psychotherapeutic drugs such as tricyclic antidepressants, phenothiazines, and other neuroleptics. The mechanism is decreased clearance due to inhibition of the CYP450 2D6 isoenzyme by darifenacin.', NULL, 'Caution is advised if darifenacin must be used concomitantly with tricyclic antidepressants, phenothiazines, or other neuroleptics.', NULL, 'Caution is advised if darifenacin must be used concomitantly with tricyclic antidepressants, phenothiazines, or other neuroleptics. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever darifenacin is added to or withdrawn from therapy. Clinicians should also be alerted to the potential for additive anticholinergic effects during concomitant administration. Patients should be advised to notify their physician promptly if they experience potential symptoms of anticholinergic intoxication such as abdominal pain, fever, heat intolerance, blurred vision, confusion, and/or hallucinations.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/79349/', '[1] "Product Information. Enablex (darifenacin)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767365205);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (79249, 'Amprenavir', 'Darifenacin', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of darifenacin, which is partially metabolized by the isoenzyme. Darifenacin is also metabolized by CYP450 2D6, but 3A4 is the major pathway of metabolism in so-called poor metabolizers of 2D6.', NULL, 'The dosage of darifenacin should not exceed 7.', NULL, 'The dosage of darifenacin should not exceed 7.5 mg/day when used with potent CYP450 3A4 inhibitors. Some authorities recommend avoiding concomitant use of darifenacin during and for 2 weeks after treatment with itraconazole.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/79350/', '[1] Niwa T, Shiraga T, Takagi A "Effect of antifungal drugs on cytochrome P450 (CYP) 2C9, CYP2C19, CYP3A4 activities in human liver microsomes." Biol Pharm Bull 28 (2005): 1805-8[2] "Product Information. Sporanox (itraconazole)." Janssen Pharmaceutica, Titusville, NJ.[3] "Product Information. Enablex (darifenacin)." Novartis Pharmaceuticals, East Hanover, NJ.[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[6] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.[7] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767365205);
