INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (79314, 'Haloperidol', 'Darifenacin', 'Moderate', 'Coadministration with darifenacin may increase the plasma concentrations of drugs that are metabolized by CYP450 2D6, including many psychotherapeutic drugs such as tricyclic antidepressants, phenothiazines, and other neuroleptics. The mechanism is decreased clearance due to inhibition of the CYP450 2D6 isoenzyme by darifenacin.', NULL, 'Caution is advised if darifenacin must be used concomitantly with tricyclic antidepressants, phenothiazines, or other neuroleptics.', NULL, 'Caution is advised if darifenacin must be used concomitantly with tricyclic antidepressants, phenothiazines, or other neuroleptics. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever darifenacin is added to or withdrawn from therapy. Clinicians should also be alerted to the potential for additive anticholinergic effects during concomitant administration. Patients should be advised to notify their physician promptly if they experience potential symptoms of anticholinergic intoxication such as abdominal pain, fever, heat intolerance, blurred vision, confusion, and/or hallucinations.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/79415/', '[1] "Product Information. Enablex (darifenacin)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767365205);
