INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (79409, 'Darifenacin', 'Pindolol', 'Moderate', 'Coadministration with darifenacin may increase the plasma concentrations of drugs that are metabolized by CYP450 2D6. The mechanism is decreased clearance due to inhibition of the isoenzyme by darifenacin. Conversely, some CYP450 2D6 substrates may also increase the plasma concentrations of darifenacin, which is itself metabolized by CYP450 2D6 and may participate in enzymatic competitive inhibition with other substrates.', NULL, 'Caution is advised if darifenacin must be used concomitantly with medications that undergo metabolism by CYP450 2D6, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if darifenacin must be used concomitantly with medications that undergo metabolism by CYP450 2D6, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever darifenacin is added to or withdrawn from therapy. Pharmacologic response to darifenacin should also be monitored more closely whenever a CYP450 2D6 inhibitor is added to or withdrawn from therapy, and the darifenacin dosage adjusted if necessary. Patients should be advised to contact their doctor if they experience undue adverse effects of darifenacin such as severe abdominal pain or constipation for 3 or more days.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/79510/', '[1] "Product Information. Enablex (darifenacin)." Novartis Pharmaceuticals, East Hanover, NJ.[2] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[3] Gilman AG, Rall TW, Nies A