INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1371402, 'Brexpiprazole', 'Methyldopa', 'Moderate', 'Phenothiazines and neuroleptic agents may potentiate the hypotensive effect of some medications secondary to their peripheral alpha-1 adrenergic blocking activity. Orthostatic hypotension and syncope associated with vasodilation may occur, particularly during initial dosing and/or parenteral administration of the phenothiazine or neuroleptic.', 'DDInter', 1767317475, 'Close clinical monitoring for development of hypotension is recommended if phenothiazines or neuroleptic agents are used in patients receiving antihypertensive medications or vasodilators. A lower starting dosage and slower titration of the phenothiazine or neuroleptic may be appropriate, especially in the elderly. Patients should be advised to avoid rising abruptly from a sitting or recumbent position and to notify their physician if they experience dizziness, lightheadedness, syncope, orthostasis, or tachycardia. Patients should also avoid driving or operating hazardous machinery.', 'Synergism', 'Close clinical monitoring for development of hypotension is recommended if phenothiazines or neuroleptic agents are used in patients receiving antihypertensive medications or vasodilators.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1371403, 'Brexpiprazole', 'Metoclopramide', 'Major', 'Coadministration of metoclopramide with phenothiazines, neuroleptics, or other antidopaminergic agents (e.g., tetrabenazine) may increase the frequency and severity of extrapyramidal reactions (i.e., acute dystonic reactions, tardive dyskinesia, akathisia, Parkinson-like symptoms) due to additive antidopaminergic effects.', 'DDInter', 1767317475, 'Due to the potential for increased risk of serious and potentially irreversible extrapyramidal reactions, metoclopramide should not be prescribed in combination with other antidopaminergic agents. In addition, metoclopramide should not be used for longer than 12 weeks except in rare cases where therapeutic benefit is anticipated to outweigh the risk of developing tardive dyskinesia.', 'Synergism', 'Due to the potential for increased risk of serious and potentially irreversible extrapyramidal reactions, metoclopramide should not be prescribed in combination with other antidopaminergic agents.', NULL, NULL, 'Major', 'pharmacodynamic');
