INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1371406, 'Brexpiprazole', 'Metyrosine', 'Moderate', 'Coadministration of metyrosine in combination with haloperidol, phenothiazines, or other dopamine antagonists may increase the risk of extrapyramidal disorders. Extrapyramidal effects have been reported during metyrosine therapy. The mechanism is inhibition of catecholamine synthesis by metyrosine. In addition, sedative effects of both drugs may be increased.', 'DDInter', 1767317475, 'When concurrent use is required, patients should be monitored for extrapyramidal symptoms and instructed to notify their physician if they experience symptoms such as bradykinesia, hypertonia, rigidity, restlessness, and dysphagia. Patients should also avoid driving or operating hazardous machinery until they know how the medications affect them.', 'Synergism', 'When concurrent use is required, patients should be monitored for extrapyramidal symptoms and instructed to notify their physician if they experience symptoms such as bradykinesia, hypertonia, rigidity, restlessness, and dysphagia.', NULL, NULL, 'Moderate', 'pharmacodynamic');
