INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1371503, 'Brexpiprazole', 'Rifabutin', 'Moderate', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of brexpiprazole, which is partially metabolized by this isoenzyme. According to product labeling, administration of brexpiprazole with a potent 3A4 inducer (rifampin) resulted in an approximately 75% decrease in AUC.', 'DDInter', 1767317475, 'During concomitant use with a potent 3A4 inducer, the usual dose of brexpiprazole should be doubled over one to two weeks. When the inducer is discontinued, the brexpiprazole dose should be decreased to its original level over one to two weeks. It is advisable to monitor patients for clinical response.', 'Metabolism', 'During concomitant use with a potent 3A4 inducer, the usual dose of brexpiprazole should be doubled over one to two weeks.', NULL, NULL, 'Moderate', 'pharmacodynamic');
