INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (79594, 'Darolutamide', 'Ritonavir', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 that can also inhibit P-glycoprotein (P-gp) may significantly increase the plasma concentrations of darolutamide, which is a substrate of both the isoenzyme and the efflux transporter.', NULL, 'Caution is advised when darolutamide is used with dual P-gp and potent CYP450 3A4 inhibitors.', NULL, 'Caution is advised when darolutamide is used with dual P-gp and potent CYP450 3A4 inhibitors. Patients should be monitored more frequently for adverse effects such as fatigue, hypertension, neutropenia and abnormal liver function tests, and the darolutamide dosage adjusted as necessary in accordance with the product labeling.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/79695/', '[1] "Product Information. Nubeqa (darolutamide)." Bayer HealthCare Pharmaceuticals Inc., Whippany, NJ.[2] "Product Information. Nubeqa (darolutamide)." Bayer HealthCare Pharmaceuticals Inc., Whippany, NJ.', 1767363022);
