INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (79659, 'Dutasteride', 'Darunavir', 'Moderate', 'Based on in vitro data, coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of dutasteride, which is metabolized by the isoenzyme.', NULL, 'The possibility of prolonged and/or increased pharmacologic effects of dutasteride should be considered during concomitant therapy with CYP450 3A4 inhibitors, particularly potent ones like itraconazole, ketoconazole, posaconazole, voriconazole, conivaptan, nefazodone, cobicistat, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics.', NULL, 'The possibility of prolonged and/or increased pharmacologic effects of dutasteride should be considered during concomitant therapy with CYP450 3A4 inhibitors, particularly potent ones like itraconazole, ketoconazole, posaconazole, voriconazole, conivaptan, nefazodone, cobicistat, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/79760/', '[1] "Product Information. Duagen (dutasteride)." GlaxoSmithKline Healthcare, Pittsburgh, PA.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (79660, 'Duvelisib', 'Darunavir', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of duvelisib, which is primarily metabolized by the isoenzyme.', NULL, 'Caution is advised when duvelisib is used with CYP450 3A4 inhibitors.', NULL, 'Caution is advised when duvelisib is used with CYP450 3A4 inhibitors. Patients should be monitored for the development of toxicities such as diarrhea, colitis, neutropenia, thrombocytopenia, pneumonitis, infections, cutaneous reactions and hepatotoxicity, and the dosing adjusted or discontinued in accordance with the duvelisib product labeling.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/79761/', '[1] "Product Information. Copiktra (duvelisib)." Verastem, Inc., Needham, MA.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (79661, 'Echinacea', 'Darunavir', 'Moderate', 'Coadministration with echinacea may alter the plasma concentrations and therapeutic effects of drugs that are substrates of CYP450 3A4. Echinacea appears to inhibit intestinal CYP450 3A4, which would lead to an increase in oral midazolam (a sensitive 3A4 substrate) bioavailability; however, plasma levels of midazolam following oral administration do not appear to be affected by echinacea. In contrast, it appears that echinacea may also induce hepatic CYP450 3A4; thereby increasing the hepatic clearance of drugs that are substrates of CYP450 3A4.', NULL, 'In general, patients should be advised to consult their healthcare provider before using any herbal or alternative medicines.', NULL, 'In general, patients should be advised to consult their healthcare provider before using any herbal or alternative medicines. If echinacea is prescribed with a drug that is a CYP450 3A4 substrate, the possibility of an altered (increased or decreased) therapeutic response should be considered. Patients should be monitored more closely following the addition or withdrawal of echinacea and the dosage of the CYP450 3A4 substrate adjusted as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/79762/', '[1] Gorski JC, Huang SM, Pinto A, et al. "The effect of echinacea (Echinacea purpurea root) on cytochrome P450 activity in vivo." Clin Pharmacol Ther 75 (2004): 89-100[2] Cerner Multum, Inc. "Australian Product Information." O 0', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (79662, 'Efavirenz', 'Darunavir', 'Moderate', 'Coadministration with efavirenz may decrease the plasma concentrations of darunavir, even in the presence of cobicistat or low-dose ritonavir as a pharmacokinetic booster. Additionally, the plasma concentration of cobicistat may be reduced. Reduced darunavir plasma levels may lead to diminished virologic response and possible resistance to darunavir. The mechanism is efavirenz induction of CYP450 3A4, the isoenzyme responsible for the metabolic clearance of darunavir and cobicistat.', NULL, 'Caution is advised if darunavir-ritonavir is coadministered with efavirenz.', NULL, 'Caution is advised if darunavir-ritonavir is coadministered with efavirenz. Antiretroviral response should be monitored closely and darunavir-ritonavir dose adjusted if necessary. Some authorities recommend a dose adjustment of darunavir 600 mg-ritonavir 100 mg twice daily. Concomitant use of darunavir-cobicistat with efavirenz is not recommended.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/79763/', '[1] "Product Information. Prezcobix (cobicistat-darunavir)." Janssen Pharmaceuticals, Titusville, NJ.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Prezista (darunavir)." Ortho Biotech Inc, Bridgewater, NJ.[4] Cerner Multum, Inc. "Australian Product Information." O 0[5] "Product Information. Sustiva (efavirenz)." DuPont Pharmaceuticals, Wilmington, DE.[6] "Product Information. Prezista (darunavir)." Ortho Biotech Inc, Bridgewater, NJ.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (79663, 'Elagolix', 'Darunavir', 'Moderate', 'Coadministration with drugs that are inducers of CYP450 3A4 may decrease the plasma concentrations of protease inhibitors (PIs), which are primarily metabolized by the isoenzyme.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, protease inhibitors should be used cautiously with agents that induce CYP450 3A4, particularly if only one PI is used in the antiretroviral regimen.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, protease inhibitors should be used cautiously with agents that induce CYP450 3A4, particularly if only one PI is used in the antiretroviral regimen. Coadministration of atazanavir without ritonavir and carbamazepine, phenobarbital, or phenytoin is not recommended. Antiretroviral response should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the antiretroviral regimen adjusted as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/79764/', '[1] Sommadossi JP "HIV protease inhibitors: pharmacologic and metabolic distinctions." AIDS 13 (1999): s29-40[2] Durant J, Clevenbergh P, Garraffo R, Halfon P, Icard S, DelGiudice P, Montagne N, Schapiro JM, Dellamonica P "Importance of protease inhibitor plasma levels in HIV-infected patients treated with genotypic-guided therapy: pharmacological data from the Viradapt Study." Aids 14 (2000): 1333-9[3] "Product Information. Reyataz (atazanavir)."