INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (79702, 'Flurazepam', 'Darunavir', 'Moderate', 'Coadministration with darunavir may increase the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme. The mechanism is decreased clearance due to inhibition of CYP450 3A4 activity by darunavir.', NULL, 'Caution is advised if darunavir must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if darunavir must be used concurrently with medications that undergo metabolism by CYP450 3A4, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever darunavir is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/79803/', '[1] "Product Information. Prezista (darunavir)." Ortho Biotech Inc, Bridgewater, NJ.[2] "Product Information. Prezista (darunavir)." Ortho Biotech Inc, Bridgewater, NJ.[3] Schubert W, Eriksson U, Edgar B, Cullberg G, Hedner T "Flavonoids in grapefruit juice inhibit the in vitro hepatic metabolism of 17B-estradiol." Eur J Drug Metab Pharmacokinet 20 (1995): 219-24[4] Weber A, Jager R, Borner A, et al. "Can grapefruit juice influence ethinyl estradiol bioavailability?" Contraception 53 (1996): 41-7', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (79703, 'Fluticasone', 'Darunavir', 'Moderate', 'Coadministration with moderate inhibitors of CYP450 3A4 may increase the systemic exposure to fluticasone following intranasal administration or oral inhalation. Fluticasone undergoes extensive first-pass and systemic metabolism via CYP450 3A4, thus inhibition of the isoenzyme may significantly increase systemic bioavailability of the drug. However, the extent of interaction may depend on the route of fluticasone administration and the specific formulation.', NULL, 'Monitor for signs and symptoms of hypercorticism if intranasal or orally inhaled fluticasone is coadministered with a moderate inhibitor of CYP450 3A4.', NULL, 'Monitor for signs and symptoms of hypercorticism if intranasal or orally inhaled fluticasone is coadministered with a moderate inhibitor of CYP450 3A4. Signs and symptoms of hypercorticism include acne, striae, thinning of the skin, easy bruising, moon facies, dorsocervical "buffalo" hump, truncal obesity, increased appetite, acute weight gain, edema, hypertension, hirsutism, hyperhidrosis, proximal muscle wasting and weakness, glucose intolerance, exacerbation of preexisting diabetes, depression, and menstrual disorders. Other systemic glucocorticoid effects may include adrenal suppression, immunosuppression, posterior subcapsular cataracts, glaucoma, bone loss, and growth retardation in children and adolescents. Signs and symptoms of adrenal insufficiency include anorexia, hypoglycemia, nausea, vomiting, weight loss, muscle wasting, fatigue, weakness, dizziness, postural hypotension, depression, and adrenal crisis manifested as inability to respond to stress (e.g., illness, infection, surgery, trauma). Systemic glucocorticoids may be necessary until adrenal function recovers.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/79804/', '[1] St Clair K, Maguire JD "Role of fluconazole in a case of rapid onset ritonavir and inhaled fluticasone-associated secondary adrenal insufficiency." Int J STD AIDS 23 (2012): 371-2[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Imbruvica (ibrutinib)." Pharmacyclics Inc, Sunnyvale, CA.[4] "Product Information. Prezista (darunavir)." Ortho Biotech Inc, Bridgewater, NJ.[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[6] Hoover WC, Britton LJ, Gardner J, Jackson T, Gutierrez H "Rapid onset of iatrogenic adrenal insufficiency in a patient with cystic fibrosis-related liver disease treated with inhaled corticosteroids and a moderate CYP3A4 inhibitor." Ann Pharmacother 45 (2011): e38[7] "Product Information. Bosulif (bosutinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (79704, 'Glasdegib', 'Darunavir', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of glasdegib, which is primarily metabolized by the isoenzyme.', NULL, 'Caution is advised when glasdegib is used with CYP450 3A4 inhibitors.', NULL, 'Caution is advised when glasdegib is used with CYP450 3A4 inhibitors. Patients should be monitored for adverse effects such as QT interval prolongation, anemia, febrile neutropenia, thrombocytopenia, hemorrhage, edema and mucositis, and the glasdegib dosage adjusted as necessary in accordance with the product labeling.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/79805/', '[1] "Product Information. Daurismo (glasdegib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.[2] "Product Information. Juxtapid (lomitapide)." Aegerion Pharmaceuticals Inc, Cambridge, MA.[3] "Product Information. Daurismo (glasdegib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.', 1767363022);
