INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (79742, 'Itraconazole', 'Darunavir', 'Moderate', 'Coadministration of ketoconazole or itraconazole with ritonavir- or cobicistat-boosted darunavir may result in increased plasma concentrations of both the azole antifungal agent and darunavir. The proposed mechanism is inhibition of CYP450 3A4, as all of these medicines are known substrates and inhibitors of the CYP450 3A4 isoenzyme.', NULL, 'During coadministration with ritonavir- or cobicistat-boosted darunavir, the dosage of ketoconazole should generally not exceed 200 mg/day so as to avoid excessive plasma drug levels, which may be associated with impairment of testosterone and cortisol production and possibly an increased risk of hepatotoxicity.', NULL, 'During coadministration with ritonavir- or cobicistat-boosted darunavir, the dosage of ketoconazole should generally not exceed 200 mg/day so as to avoid excessive plasma drug levels, which may be associated with impairment of testosterone and cortisol production and possibly an increased risk of hepatotoxicity. Some authorities recommend that the dosage of itraconazole should also be limited to 200 mg per day when prescribed with ritonavir- or cobicistat-boosted darunavir.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/79843/', '[1] "Product Information. Prezista (darunavir)." Ortho Biotech Inc, Bridgewater, NJ.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Prezista (darunavir)." Ortho Biotech Inc, Bridgewater, NJ.', 1767363022);
