INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (79749, 'Darunavir', 'Ketoconazole', 'Moderate', 'Coadministration of ketoconazole or itraconazole with ritonavir- or cobicistat-boosted darunavir may result in increased plasma concentrations of both the azole antifungal agent and darunavir. The proposed mechanism is inhibition of CYP450 3A4, as all of these medicines are known substrates and inhibitors of the CYP450 3A4 isoenzyme.', NULL, 'During coadministration with ritonavir- or cobicistat-boosted darunavir, the dosage of ketoconazole should generally not exceed 200 mg/day so as to avoid excessive plasma drug levels, which may be associated with impairment of testosterone and cortisol production and possibly an increased risk of hepatotoxicity.', NULL, 'During coadministration with ritonavir- or cobicistat-boosted darunavir, the dosage of ketoconazole should generally not exceed 200 mg/day so as to avoid excessive plasma drug levels, which may be associated with impairment of testosterone and cortisol production and possibly an increased risk of hepatotoxicity. Some authorities recommend that the dosage of itraconazole should also be limited to 200 mg per day when prescribed with ritonavir- or cobicistat-boosted darunavir.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/79850/', '[1] "Product Information. Prezista (darunavir)." Ortho Biotech Inc, Bridgewater, NJ.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Prezista (darunavir)." Ortho Biotech Inc, Bridgewater, NJ.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (79750, 'Darunavir', 'Lapatinib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of lapatinib, which is primarily metabolized by the isoenzyme.', NULL, 'Caution is advised when lapatinib is prescribed with CYP450 3A4 inhibitors.', NULL, 'Caution is advised when lapatinib is prescribed with CYP450 3A4 inhibitors. Pharmacologic response to lapatinib should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the lapatinib dosage adjusted as necessary. Patients should be advised to contact their physician if they experience increased adverse effects of lapatinib such as diarrhea, nausea, vomiting, dyspepsia, and fatigue.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/79851/', '[1] "Product Information. Tykerb (lapatinib)." Novartis Pharmaceuticals, East Hanover, NJ.[2] "Product Information. Accolate (zafirlukast)." Zeneca Pharmaceuticals, Wilmington, DE.[3] "Product Information. Tykerb (lapatinib)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (79751, 'Darunavir', 'Larotrectinib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4, P-glycoprotein (P-gp), and/or breast cancer resistance protein (BCRP) may increase the plasma concentrations of larotrectinib.', NULL, 'Caution is advised when larotrectinib is used with CYP450 3A4, P-gp, and/or BCRP inhibitors.', NULL, 'Caution is advised when larotrectinib is used with CYP450 3A4, P-gp, and/or BCRP inhibitors. Patients should be monitored for adverse effects such as neurotoxicity (delirium, dysarthria, dizziness, gait disturbance, paraesthesia, encephalopathy, memory impairment, tremor) and hepatotoxicity (elevations in liver transaminases), and the larotrectinib dosage adjusted as necessary in accordance with the product labeling.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/79852/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Vitrakvi (larotrectinib)." Bayer Pharmaceutical Inc, West Haven, CT.[4] "Product Information. Vitrakvi (larotrectinib)." Bayer Pharmaceutical Inc, West Haven, CT.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (79752, 'Darunavir', 'Lefamulin', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 and/or P-gp may increase the plasma concentrations of lefamulin, which is a substrate of both the isoenzyme and efflux transporter.', NULL, 'Caution is advised when lefamulin is used with CYP450 3A4 and/or P-gp inhibitors.', NULL, 'Caution is advised when lefamulin is used with CYP450 3A4 and/or P-gp inhibitors. Patients should be monitored for increased adverse effects and seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/79853/', '[1] "Product Information. Xenleta (lefamulin)." Nabriva Therapeutics US, Inc., King of Prussia, PA.[2] "Product Information. Xenleta (lefamulin)." Nabriva Therapeutics US, Inc., King of Prussia, PA.[3] "Product Information. Mavyret (glecaprevir-pibrentasvir)." Abbott Pharmaceutical, Abbott Park, IL.[4] "Product Information. Xenleta (lefamulin)." Nabriva Therapeutics US, Inc., King of Prussia, PA.', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (79753, 'Darunavir', 'Leflunomide', 'Major', 'The recent, concomitant, or subsequent use (without the recommended leflunomide washout period or procedure) of other agents known to induce hepatotoxicity may potentiate the risk of liver injury associated with leflunomide. The risk is thought to extend to teriflunomide, its principal active metabolite, because recommended dosages of both result in a similar range of plasma concentrations of teriflunomide.', NULL, 'Caution is advised if leflunomide or teriflunomide must be used in patients who are currently receiving or have recently received treatment with other hepatotoxic agents (e.', NULL, 'Caution is advised if leflunomide or teriflunomide must be used in patients who are currently receiving or have recently received treatment with other hepatotoxic agents (e.g., acetaminophen; alcohol; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; interferons; ketolide and macrolide antibiotics; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice), and vice versa. Liver enzymes and bilirubin should be measured prior to initiation of leflunomide/teriflunomide therapy and at least monthly for the first six months of treatment and every 6 to 8 weeks thereafter. Patients with preexisting liver disease or elevated baseline liver enzymes (i.e., ALT greater than two times ULN) should not receive leflunomide or teriflunomide. Patients who develop elevated serum ALT greater than three times ULN while receiving these medications should discontinue treatment and be given washout procedures with cholestyramine or activated charcoal to accelerate elimination of leflunomide''s active metabolite from plasma, which otherwise may take up to two years. Follow-up monitoring should be conducted at least weekly until the ALT value is within normal range, and washout procedures repeated as necessary. All patients treated with leflunomide or teriflunomide should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/79854/', '[1] EMEA "EMEA public statement on leflunomide (ARAVA) - severe and serious hepatic reactions. Available from URL: http://www.eudra.org/emea.html." ([1999 Sept 2]):[2] "Product Information. Arava (leflunomide)." Hoechst Marion-Roussel Inc, Kansas City, MO.[3] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (79754, 'Darunavir', 'Lemborexant', 'Major', 'Coadministration with potent or moderate inhibitors of CYP450 3A4 may increase the plasma concentrations of lemborexant, which is primarily metabolized by the isoenzyme.', NULL, 'Concomitant use of lemborexant with potent or moderate CYP450 3A4 inhibitors should be avoided.', NULL, 'Concomitant use of lemborexant with potent or moderate CYP450 3A4 inhibitors should be avoided.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/79855/', '[1] "Product Information. Dayvigo (lemborexant)." Eisai Inc, Woodcliff Lake, NJ.[2] "Product Information. Dayvigo (lemborexant)." Eisai Inc, Woodcliff Lake, NJ.', 1767363022);
