INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (79835, 'Darunavir', 'Pravastatin', 'Moderate', 'Coadministration with darunavir/ritonavir may increase the plasma concentrations of pravastatin. The mechanism of interaction has not been described.', NULL, 'Because high levels of HMG-CoA reductase inhibitory activity in plasma is associated with an increased risk of musculoskeletal toxicity including severe myopathy and rhabdomyolysis, pravastatin should be initiated at the lowest possible dosage when prescribed with darunavir/ritonavir.', NULL, 'Because high levels of HMG-CoA reductase inhibitory activity in plasma is associated with an increased risk of musculoskeletal toxicity including severe myopathy and rhabdomyolysis, pravastatin should be initiated at the lowest possible dosage when prescribed with darunavir/ritonavir. The dosage may be increased gradually based on clinical response. All patients treated with HMG-CoA reductase inhibitors should be advised to promptly report any unexplained muscle pain, tenderness, or weakness, particularly if accompanied by malaise or fever. Therapy should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/79936/', '[1] "Product Information. Pravachol (pravastatin)." Bristol-Myers Squibb, Princeton, NJ.[2] "Product Information. Prezista (darunavir)." Ortho Biotech Inc, Bridgewater, NJ.[3] "Product Information. Prezista (darunavir)." Ortho Biotech Inc, Bridgewater, NJ.', 1767363022);
