INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (79891, 'Darunavir', 'Tacrolimus', 'Major', 'Coadministration with protease inhibitors (PIs) may significantly increase the blood concentrations of tacrolimus. The proposed mechanism is PI inhibition of intestinal and hepatic CYP450 3A4, the isoenzyme responsible for the metabolic clearance of tacrolimus. Enhanced tacrolimus oral bioavailability due to inhibition of intestinal P-glycoprotein (P-gp) efflux transporter may also contribute.', NULL, 'Caution is advised when tacrolimus is used with protease inhibitors (PIs).', NULL, 'Caution is advised when tacrolimus is used with protease inhibitors (PIs). Dosage reduction and/or prolongation of the dosing interval for tacrolimus will likely be required. Tacrolimus blood levels and renal function should be checked frequently and the dosage adjusted accordingly, particularly following initiation or discontinuation of PI therapy. Patients should be closely monitored for development of serious adverse effects such as nephrotoxicity, lymphoma and other malignancies, infections, diabetes, neurotoxicity (tremor, paraesthesia, encephalopathy, delirium, coma), hyperkalemia, QT prolongation, myocardial hypertrophy, and hypertension. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/79992/', '[1] Cakaloglu Y, Tredger JM, Devlin J, Williams R "Importance of cytochrome p-450IIIA activity in determining dosage and blood levels of FK 506 and cyclosporine in liver transplant recipients." Hepatology 20 (1994): 309-16[2] Jain AK, Venkataramanan R, Shapiro R, et al. "Interaction between tacrolimus and antiretroviral agents in human immunodeficiency virus-positive liver and kidney transplantation patients." Transplant Proc 34 (2002): 1540-1[3] Teicher E, Vincent I, Bonhomme-Faivre L, et al. "Effect of Highly Active Antiretroviral Therapy on Tacrolimus Pharmacokinetics in Hepatitis C Virus and HIV Co-Infected Liver Transplant Recipients in the ANRS HC-08 Study." Clin Pharmacokinet 46 (2007): 941-52[4] Barau C, Blouin P, Creput C, Taburet AM, Durrbach A, Furlan V "Effect of coadministered HIV-protease inhibitors on tacrolimus and sirolimus blood concentrations in a kidney transplant recipient." Fundam Clin Pharmacol 23 (2009): 423-5[5] "Product Information. Fortovase (saquinavir)" Roche Laboratories, Nutley, NJ.[6] Pea F, Tavio M, Pavan F, et al. "Drop in trough blood concentrations of tacrolimus after switching from nelfinavir to fosamprenavir in four HIV-infected liver transplant patients." Antivir Ther 13 (2008): 739-42[7] "Product Information. Reyataz (atazanavir)." Bristol-Myers Squibb, Princeton, NJ.[8] "Product Information. Lexiva (fosamprenavir)." GlaxoSmithKline, Research Triangle Park, NC.[9] Jain AK, Venkataramanan R, Shapiro R, et al. "The interaction between antiretroviral agents and tacrolimus in liver and kidney transplant patients." Liver Transpl 8 (2002): 841-5[10] Tsapepas DS, Webber AB, Aull MJ, Figueiro JM, Saal SD "Managing the atazanavir-tacrolimus drug interaction in a renal transplant recipient." Am J Health Syst Pharm 68 (2011): 138-42[11] "Product Information. Prezista (darunavir)." Ortho Biotech Inc, Bridgewater, NJ.[12] "Product Information. Crixivan (indinavir)." Merck & Co, Inc, West Point, PA.[13] "Product Information. Prograf (tacrolimus)." Fujisawa, Deerfield, IL.[14] "Product Information. Agenerase (amprenavir)." Glaxo Wellcome, Research Triangle Pk, NC.[15] Mertz D, Battegay M, Marzolini C, Mayr M "Drug-Drug Interaction in a Kidney Transplant Recipient Receiving HIV Salvage Therapy and Tacrolimus." Am J Kidney Dis (2009):[16] "Product Information. Viracept (nelfinavir)." Agouron Pharma Inc, La Jolla, CA.[17] "Product Information. Norvir (ritonavir)." Abbott Pharmaceutical, Abbott Park, IL.[18] Jain AB, Venkataramanan R, Eghtesad B, et al. "Effect of coadministered lopinavir and ritonavir (Kaletra) on tacrolimus blood concentration in liver transplantation patients." Liver Transpl 9 (2003): 954-60[19] Schonder KS, Shullo MA, Okusanya O "Tacrolimus and lopinavir/ritonavir interaction in liver transplantation." Ann Pharmacother 37 (2003): 1793-6[20] Hooks MA "Tacrolimus, a new immunosuppressant--a review of the literature." Ann Pharmacother 28 (1994): 501-11[21] "Product Information. Prograf (tacrolimus)." Fujisawa, Deerfield, IL.[22] Shelton MJ, Wynn HE, Newitt RG, DiFrancesco R "Effects of grapefruit juice on pharmacokinetic exposure to indinavir in HIV-positive subjects." J Clin Pharmacol 41 (2001): 435-42[23] "Product Information. Crixivan (indinavir)." Merck & Co, Inc, West Point, PA.[24] Yeh KC, Deutsch PJ, Haddix H, Hesney M, Hoagland V, Ju WD, Justice SJ, Osborne B, Sterrett AT, Stone JA, Woolf E, Waldman S "Single-dose pharmacokinetics of indinavir and the effect of food." Antimicrob Agents Chemother 42 (1998): 332-8', 1767363022);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (79892, 'Darunavir', 'Tadalafil', 'Moderate', 'Coadministration with protease inhibitors (PIs) may increase the plasma concentrations of tadalafil. The mechanism is PI inhibition of tadalafil metabolism via CYP450 3A4. Although not specifically studied, other HIV protease inhibitors are expected to increase tadalafil exposure.', NULL, 'Caution is advised if tadalafil is administered in combination with protease inhibitors.', NULL, 'Caution is advised if tadalafil is administered in combination with protease inhibitors. For the treatment of erectile dysfunction, the dosage of tadalafil should not exceed 10 mg once every 72 hours when used on an as-needed basis in patients receiving PI therapy. When given for once-daily use in the treatment of erectile dysfunction or benign prostatic hyperplasia, the maximum recommended dose is 2.5 mg. For the treatment of pulmonary arterial hypertension, tadalafil should be started at 20 mg once daily in patients who have been receiving ritonavir-boosted PI therapy for at least one week. The dosage may be increased to 40 mg once daily based upon individual tolerability. The use of tadalafil for PAH should be avoided during the initiation of ritonavir-boosted PI therapy. The manufacturers recommend that tadalafil be discontinued at least 24 hours prior to initiating the PIs. After at least one week, tadalafil may be resumed at 20 mg once daily, and the dosage increased to 40 mg once daily based upon individual tolerability. There is no need to discontinue tadalafil when initiating PI therapy that does not contain ritonavir as a pharmacokinetic booster, nor wait at least one week after PI therapy to start tadalafil. The tadalafil dosage should be initiated at or adjusted to 20 mg once daily when coadministered with nonritonavir-boosted PI regimens, then increased to 40 mg as tolerated. All patients treated with tadalafil should be advised to promptly notify their physician if they experience pain or tightness in the chest or jaw, irregular heartbeat, nausea, shortness of breath, visual disturbances, syncope, or prolonged erection (greater than 4 hours).', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/79993/', '[1] "Product Information. Adcirca (tadalafil)." United Therapeutics Corporation, Silver Spring, MD.[2] "Product Information. Reyataz (atazanavir)." Bristol-Myers Squibb, Princeton, NJ.[3] "Product Information. Aptivus (tipranavir)." Boehringer-Ingelheim, Ridgefield, CT.[4] "Product Information. Lexiva (fosamprenavir)." GlaxoSmithKline, Research Triangle Park, NC.[5] "Product Information. Cialis (tadalafil)." Lilly, Eli and Company, Indianapolis, IN.[6] "Product Information. Prezista (darunavir)." Ortho Biotech Inc, Bridgewater, NJ.[7] "Product Information. Viracept (nelfinavir)." Agouron Pharma Inc, La Jolla, CA.[8] "Product Information. Kaletra (lopinavir-ritonavir)" Abbott Pharmaceutical, Abbott Park, IL.[9] "Product Information. Crixivan (indinavir)." Merck & Co, Inc, West Point, PA.[10] "Product Information. Invirase (saquinavir)." Roche Laboratories, Nutley, NJ.[11] Loulergue P, Gaillard R, Mir O "Interaction involving tadalafil and CYP3A4 inhibition by ritonavir." Scand J Infect Dis 43 (2011): 239-40[12] "Product Information. Cialis (tadalafil)." Lilly, Eli and Company, Indianapolis, IN.[13] "Product Information. Adcirca (tadalafil)." United Therapeutics Corporation, Silver Spring, MD.[14] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[15] "Product Information. Aptivus (tipranavir)." Boehringer-Ingelheim, Ridgefield, CT.[16] Cerner Multum, Inc. "Australian Product Information." O 0[17] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767363022);
