INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1371729, 'Fexofenadine', 'Brigatinib', 'Moderate', 'Coadministration with brigatinib may increase the plasma concentrations and risk of adverse effects of drugs that are substrates of P-glycoprotein (P-gp), breast cancer resistance protein (BCRP), organic cation transporter 1 (OCT1), and/or multidrug and toxin extrusion protein 1 (MATE1), and 2K (MATE2K). The proposed mechanism, based on in vitro data, is decreased drug clearance due to brigatinib-mediated inhibition of these transport proteins.', 'DDInter', 1767317475, 'Caution is advised if brigatinib is used concomitantly with drugs that are substrates of P-gp, BCRP, OCT 1, MATE 1, and/or MATE 2K transport proteins, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever brigatinib is added to or withdrawn from therapy with these drugs. Patients should be monitored for the development of adverse effects.', 'Excretion', 'Caution is advised if brigatinib is used concomitantly with drugs that are substrates of P-gp, BCRP, OCT 1, MATE 1, and/or MATE 2K transport proteins, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1371730, 'Fidaxomicin', 'Brigatinib', 'Minor', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of fidaxomicin and its main metabolite, OP-1118, both of which are substrates of the intestinal efflux transporter. Theoretically, concentrations of fidaxomicin and OP-1118 may also be decreased at the site of action (i.e., gastrointestinal tract) due to P-gp inhibition.', 'DDInter', 1767317475, '-', 'Absorption', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
