INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1371774, 'Irinotecan', 'Brigatinib', 'Moderate', 'Coadministration with brigatinib may increase the plasma concentrations and risk of adverse effects of drugs that are substrates of P-glycoprotein (P-gp), breast cancer resistance protein (BCRP), organic cation transporter 1 (OCT1), and/or multidrug and toxin extrusion protein 1 (MATE1), and 2K (MATE2K). The proposed mechanism, based on in vitro data, is decreased drug clearance due to brigatinib-mediated inhibition of these transport proteins.', 'DDInter', 1767317475, 'Caution is advised if brigatinib is used concomitantly with drugs that are substrates of P-gp, BCRP, OCT 1, MATE 1, and/or MATE 2K transport proteins, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever brigatinib is added to or withdrawn from therapy with these drugs. Patients should be monitored for the development of adverse effects.', 'Excretion', 'Caution is advised if brigatinib is used concomitantly with drugs that are substrates of P-gp, BCRP, OCT 1, MATE 1, and/or MATE 2K transport proteins, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1371775, 'Irinotecan (liposomal)', 'Brigatinib', 'Moderate', 'Coadministration with brigatinib may increase the plasma concentrations and risk of adverse effects of drugs that are substrates of P-glycoprotein (P-gp), breast cancer resistance protein (BCRP), organic cation transporter 1 (OCT1), and/or multidrug and toxin extrusion protein 1 (MATE1), and 2K (MATE2K). The proposed mechanism, based on in vitro data, is decreased drug clearance due to brigatinib-mediated inhibition of these transport proteins.', 'DDInter', 1767317475, 'Caution is advised if brigatinib is used concomitantly with drugs that are substrates of P-gp, BCRP, OCT 1, MATE 1, and/or MATE 2K transport proteins, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever brigatinib is added to or withdrawn from therapy with these drugs. Patients should be monitored for the development of adverse effects.', 'Excretion', 'Caution is advised if brigatinib is used concomitantly with drugs that are substrates of P-gp, BCRP, OCT 1, MATE 1, and/or MATE 2K transport proteins, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1371776, 'Isavuconazonium', 'Brigatinib', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of brigatinib, which is partially metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Concomitant use of moderate CYP450 3A4 inhibitors should generally be avoided during therapy with brigatinib. If coadministration is required, the brigatinib dose should be reduced by approximately 40% (i.e., from 180 mg to 120 mg, 120 mg to 90 mg, or from 90 mg to 60 mg). There are authorities (AU and UK) that state that no dose adjustment is required, however, patients should be closely monitored. Patients should be monitored for increased brigatinib adverse reactions such as nausea, vomiting, diarrhea, hypertension, bradycardia, hyperglycaemia, visual disturbances, lymphopenia, anemia, and elevations in pancreatic enzymes and creatine phosphokinase. If a dose adjustment is made, the brigatinib dose that was tolerated previously may be resumed following discontinuation of the CYP450 3A4 inhibitor.', 'Metabolism', 'Concomitant use of moderate CYP450 3A4 inhibitors should generally be avoided during therapy with brigatinib.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1371777, 'Isoflurophate (ophthalmic)', 'Brigatinib', 'Moderate', 'Coadministration of brigatinib with other agents that can also slow the heart rate may potentiate the risk of severe bradycardia.', 'DDInter', 1767317475, 'Caution is advised if brigatinib is used concomitantly with beta-blockers, calcium channel blockers, digitalis, or other drugs that can slow the heart rate or atrioventricular conduction such as alectinib, atazanavir, fingolimod, flecainide, ivabradine, lacosamide, lithium, mefloquine, moricizine, propafenone, succinylcholine, thalidomide, H2-receptor antagonists, tricyclic antidepressants, and anticholinesterase or cholinergic agents. Heart rate and blood pressure should be monitored frequently, and patients should be advised to seek medical attention if they experience dizziness, lightheadedness, fainting, or irregular heartbeat.', 'Synergism', 'Caution is advised if brigatinib is used concomitantly with beta-blockers, calcium channel blockers, digitalis, or other drugs that can slow the heart rate or atrioventricular conduction such as alectinib, atazanavir, fingolimod, flecainide, ivabradine, lacosamide, lithium, mefloquine, moricizine, propafenone, succinylcholine, thalidomide, H2-receptor antagonists, tricyclic antidepressants, and anticholinesterase or cholinergic agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
