INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1371835, 'Brigatinib', 'Miglitol', 'Moderate', 'The efficacy of insulin and other antidiabetic agents may be diminished by certain drugs, including atypical antipsychotics, corticosteroids, diuretics, estrogens, gonadotropin-releasing hormone agonists, human growth hormone, phenothiazines, progestins, protease inhibitors, sympathomimetic amines, thyroid hormones, L-asparaginase, alpelisib, copanlisib, danazol, diazoxide, isoniazid, megestrol, omacetaxine, phenytoin, tagraxofusp, temsirolimus, as well as pharmacologic dosages of nicotinic acid and adrenocorticotropic agents. These drugs may interfere with blood glucose control because they can cause hyperglycemia, glucose intolerance, new-onset diabetes mellitus, and/or exacerbation of preexisting diabetes.', 'DDInter', 1767317475, 'Close clinical monitoring of glycemic control is recommended following initiation or discontinuation of these drugs, and the dosages of concomitant antidiabetic agents adjusted as necessary. Patients should be advised to notify their physician if their blood glucose is consistently high or if they experience symptoms of severe hyperglycemia such as excessive thirst and increases in the volume or frequency of urination.', 'Antagonism', 'Close clinical monitoring of glycemic control is recommended following initiation or discontinuation of these drugs, and the dosages of concomitant antidiabetic agents adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1371836, 'Brigatinib', 'Mirtazapine', 'Moderate', 'Coadministration of brigatinib with other agents that can also slow the heart rate may potentiate the risk of severe bradycardia.', 'DDInter', 1767317475, 'Caution is advised if brigatinib is used concomitantly with beta-blockers, calcium channel blockers, digitalis, or other drugs that can slow the heart rate or atrioventricular conduction such as alectinib, atazanavir, fingolimod, flecainide, ivabradine, lacosamide, lithium, mefloquine, moricizine, propafenone, succinylcholine, thalidomide, H2-receptor antagonists, tricyclic antidepressants, and anticholinesterase or cholinergic agents. Heart rate and blood pressure should be monitored frequently, and patients should be advised to seek medical attention if they experience dizziness, lightheadedness, fainting, or irregular heartbeat.', 'Synergism', 'Caution is advised if brigatinib is used concomitantly with beta-blockers, calcium channel blockers, digitalis, or other drugs that can slow the heart rate or atrioventricular conduction such as alectinib, atazanavir, fingolimod, flecainide, ivabradine, lacosamide, lithium, mefloquine, moricizine, propafenone, succinylcholine, thalidomide, H2-receptor antagonists, tricyclic antidepressants, and anticholinesterase or cholinergic agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1371837, 'Brigatinib', 'Mitotane', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of brigatinib, which is partially metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Concomitant use of brigatinib with potent CYP450 3A4 inducers should generally be avoided.', 'Metabolism', 'Concomitant use of brigatinib with potent CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1371838, 'Brigatinib', 'Mitoxantrone', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of mitoxantrone, which is a substrate of the efflux transporter also known as ABCB1 or MDR1. The interaction has been studied with cyclosporine, a potent P-gp inhibitor.', 'DDInter', 1767317475, 'Caution is advised if mitoxantrone is prescribed in combination with a P-gp inhibitor. Patients should be closely monitored for increased adverse effects including cardiotoxicity and myelosuppression.', 'Others', 'Caution is advised if mitoxantrone is prescribed in combination with a P-gp inhibitor.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1371839, 'Brigatinib', 'Modafinil', 'Major', 'Coadministration with moderate inducers of CYP450 3A4 may decrease the plasma concentrations of brigatinib, which is partially metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Concomitant use of brigatinib with moderate CYP450 3A4 inducers should generally be avoided. If coadministration cannot be avoided, the dose of brigatinib should be increased in 30 mg increments as tolerated every 7 days up to a maximum of twice the brigatinib dose that was tolerated prior to initiating the moderate CYP450 3A4 inducer. After discontinuation of the moderate CYP450 3A4 inducer, the brigatinib dose tolerated prior to initiating the moderate CYP450 3A4 inducer should be resumed. The potential for diminished pharmacologic effects of brigatinib should be considered during coadministration with CYP450 3A4 inducers.', 'Metabolism', 'Concomitant use of brigatinib with moderate CYP450 3A4 inducers should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1371840, 'Brigatinib', 'Monomethyl fumarate', 'Moderate', 'The use of monomethyl fumarate (MMF) or its prodrugs, dimethyl fumarate and diroximel fumarate, with other immunosuppressive or myelosuppressive agents may increase the risk of infections.', 'DDInter', 1767317475, 'The safety and efficacy of monomethyl fumarate, dimethyl fumarate, or diroximel fumarate in combination with other immunosuppressive or myelosuppressive agents have not been evaluated. Until further information is available, concomitant use should be avoided if possible.', 'Synergism', 'The safety and efficacy of monomethyl fumarate, dimethyl fumarate, or diroximel fumarate in combination with other immunosuppressive or myelosuppressive agents have not been evaluated.', NULL, NULL, 'Moderate', 'pharmacodynamic');
