INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1371978, 'Brigatinib', 'Tolvaptan', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the oral bioavailability of tolvaptan, which has been shown in vitro to be a substrate of the efflux transporter.', 'DDInter', 1767317475, 'Caution is advised if tolvaptan is prescribed in combination with P-glycoprotein inhibitors, especially potent ones such as amiodarone, cyclosporine, or quinidine. Serum sodium concentration and neurologic status should be closely monitored, and the tolvaptan dosage adjusted accordingly. Too rapid correction of hyponatremia increases the risk of osmotic demyelination syndrome, which is associated with dysarthria, mutism, dysphagia, lethargy, affective changes, spastic quadriparesis, seizures, coma, and death. Discontinuation or interruption of tolvaptan therapy should be considered in patients who develop too rapid a rise in serum sodium.', 'Absorption', 'Caution is advised if tolvaptan is prescribed in combination with P-glycoprotein inhibitors, especially potent ones such as amiodarone, cyclosporine, or quinidine.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1371979, 'Brigatinib', 'Topotecan', 'Moderate', 'Coadministration with brigatinib may increase the plasma concentrations and risk of adverse effects of drugs that are substrates of P-glycoprotein (P-gp), breast cancer resistance protein (BCRP), organic cation transporter 1 (OCT1), and/or multidrug and toxin extrusion protein 1 (MATE1), and 2K (MATE2K). The proposed mechanism, based on in vitro data, is decreased drug clearance due to brigatinib-mediated inhibition of these transport proteins.', 'DDInter', 1767317475, 'Caution is advised if brigatinib is used concomitantly with drugs that are substrates of P-gp, BCRP, OCT 1, MATE 1, and/or MATE 2K transport proteins, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever brigatinib is added to or withdrawn from therapy with these drugs. Patients should be monitored for the development of adverse effects.', 'Excretion', 'Caution is advised if brigatinib is used concomitantly with drugs that are substrates of P-gp, BCRP, OCT 1, MATE 1, and/or MATE 2K transport proteins, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1371980, 'Brigatinib', 'Toremifene', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of toremifene. According to the product labeling, toremifene is primarily metabolized by CYP450 3A4 to N-demethyltoremifene, an antiestrogenic metabolite with weak in vivo antitumor potency but whose serum concentrations are 2 to 4 times higher than those of toremifene at steady state. The extent to which other, less potent CYP450 3A4 inducers may affect toremifene is unknown.', 'DDInter', 1767317475, 'The potential for diminished pharmacologic effects of toremifene should be considered during coadministration with CYP450 3A4 inducers. Alternative treatments or a dose adjustment of toremifene may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of toremifene should be considered during coadministration with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
