INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1371985, 'Brigatinib', 'Trichophyton mentagrophytes', 'Moderate', 'Immunosuppressed patients may have diminished response to diagnostic skin test antigens due to suppression of cell-mediated, delayed-type hypersensitivity. Falsely insignificant or false-negative results may occur in such patients, which may include those who have recently received or are receiving immunosuppressive agents, antilymphocyte globulins, alkylating agents, antimetabolites, radiation, some antirheumatic agents, high dosages of corticosteroids or adrenocorticotropic agents, or long-term topical or inhaled corticosteroids.', 'DDInter', 1767317475, 'Clinicians should be aware of the potential for falsely insignificant or false-negative results when administering diagnostic skin test antigens to patients treated with immunosuppressive agents.', 'Others', 'Clinicians should be aware of the potential for falsely insignificant or false-negative results when administering diagnostic skin test antigens to patients treated with immunosuppressive agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1371986, 'Brigatinib', 'Trimipramine', 'Moderate', 'Coadministration of brigatinib with other agents that can also slow the heart rate may potentiate the risk of severe bradycardia.', 'DDInter', 1767317475, 'Caution is advised if brigatinib is used concomitantly with beta-blockers, calcium channel blockers, digitalis, or other drugs that can slow the heart rate or atrioventricular conduction such as alectinib, atazanavir, fingolimod, flecainide, ivabradine, lacosamide, lithium, mefloquine, moricizine, propafenone, succinylcholine, thalidomide, H2-receptor antagonists, tricyclic antidepressants, and anticholinesterase or cholinergic agents. Heart rate and blood pressure should be monitored frequently, and patients should be advised to seek medical attention if they experience dizziness, lightheadedness, fainting, or irregular heartbeat.', 'Synergism', 'Caution is advised if brigatinib is used concomitantly with beta-blockers, calcium channel blockers, digitalis, or other drugs that can slow the heart rate or atrioventricular conduction such as alectinib, atazanavir, fingolimod, flecainide, ivabradine, lacosamide, lithium, mefloquine, moricizine, propafenone, succinylcholine, thalidomide, H2-receptor antagonists, tricyclic antidepressants, and anticholinesterase or cholinergic agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1371987, 'Brigatinib', 'Troglitazone', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of brigatinib, which is partially metabolized by the isoenzyme.', 'DDInter', 1767317475, 'The potential for diminished pharmacologic effects of brigatinib should be considered during coadministration with CYP450 3A4 inducers. Alternative treatments may be required if an interaction is suspected.', 'Metabolism', 'The potential for diminished pharmacologic effects of brigatinib should be considered during coadministration with CYP450 3A4 inducers.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1371988, 'Brigatinib', 'Troleandomycin', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of brigatinib, which is partially metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Concomitant use of brigatinib with potent CYP450 3A4 inhibitors should generally be avoided. If coadministration is required, the brigatinib once daily dose should be reduced by approximately 50% (i.e., from 180 mg to 90 mg, or from 90 mg to 60 mg). Patients should be closely monitored for increased adverse reactions such as nausea, vomiting, diarrhea, hypertension, bradycardia, hyperglycemia, visual disturbances, lymphopenia, anemia, and elevations in pancreatic enzymes and creatine phosphokinase. Following discontinuation of the CYP450 3A4 inhibitor, the brigatinib dose that was tolerated previously may be resumed.', 'Metabolism', 'Concomitant use of brigatinib with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
