INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (161075, 'Ribociclib', 'Phenylbutazone', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of ribociclib, which is a substrate of the isoenzyme.', NULL, 'The potential for diminished therapeutic effects of ribociclib should be considered when prescribed with inducers of CYP450 3A4, particularly moderate inducers including efavirenz and nevirapine.', NULL, 'The potential for diminished therapeutic effects of ribociclib should be considered when prescribed with inducers of CYP450 3A4, particularly moderate inducers including efavirenz and nevirapine. Pharmacologic effects of ribociclib should be monitored more closely whenever an inducer is added to or withdrawn from therapy. Alternative treatment may be required if an interaction is suspected.', 'Metabolism', 'Acetylsalicylic acid, Rabeprazole, Ketoprofen, Diclofenac, Piroxicam, Rofecoxib, Sulindac, Meclofenamic acid, Etodolac, Ketorolac, Misoprostol, More', 'Futibatinib, Fedratinib, Lenvatinib, Erdafitinib, Pralsetinib, Dabrafenib, Asciminib, Vandetanib, Neratinib, Afatinib, Trametinib, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/161248/', '[1] "Product Information. Kisqali (ribociclib)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] "Product Information. Xermelo (telotristat ethyl)." Lexicon Pharmaceuticals, Inc., The Woodlands, TX.[5] "Product Information. Kisqali (ribociclib)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (161076, 'Ribociclib', 'Polatuzumab vedotin', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase exposure to unconjugated monomethyl auristatin E (MMAE), the anti-mitotic and cytotoxic component of polatuzumab vedotin. MMAE is a substrate for CYP450 3A4.', NULL, 'Caution is advised when polatuzumab vedotin is used concomitantly with CYP450 3A4 inhibitors.', NULL, 'Caution is advised when polatuzumab vedotin is used concomitantly with CYP450 3A4 inhibitors. Patients should be monitored for development or exacerbation of toxicities such as peripheral neuropathy, myelosuppression, opportunistic infections, tumor lysis syndrome and hepatotoxicity, and the dosing of polatuzumab vedotin adjusted or withheld as necessary in accordance with the product labeling.', 'Metabolism', 'Acalabrutinib, Dacomitinib, Tepotinib, Avapritinib, Bosutinib, Vemurafenib, Selumetinib, Vandetanib, Cobimetinib, Capmatinib, Ibrutinib, More', 'Pertuzumab, Cetuximab, Avelumab, Panitumumab, Obinutuzumab, Bevacizumab, Necitumumab, Dostarlimab, Ramucirumab, Atezolizumab, Amivantamab, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/161249/', '[1] "Product Information. Polivy (polatuzumab vedotin)." Genentech, South San Francisco, CA.', 1767369485);
