INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (161295, 'Halothane', 'Rifampicin', 'Major', 'The hepatotoxic effects of rifampin and isoniazid appear to be increased when these drugs are administered together after halothane anesthesia. Cases of hepatotoxicity and hepatic encephalopathy, one of which was fatal, have been reported. A similar reaction may occur with rifabutin. The mechanism and causality have not been definitely determined since rifampin, isoniazid or halothane alone are potentially hepatotoxic.', NULL, 'Until more information is available, it is best to avoid this combination after halothane anesthesia.', NULL, 'Until more information is available, it is best to avoid this combination after halothane anesthesia.', 'Synergism', 'Capreomycin, Pyrazinamide, Rifamycin, Streptomycin, Trimethoprim, Cycloserine, Ethambutol, Pyridoxine, Ethionamide, Pretomanid, Aminosalicylic acid', 'Sodium oxybate, Isoflurane, Nitrous oxide, Desflurane, Sufentanil, Enflurane, Esketamine, Sevoflurane, Etomidate, Ketamine', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/161468/', '[1] Venkatesan K "Pharmacokinetic drug interactions with rifampicin." Clin Pharmacokinet 22 (1992): 47-65[2] Pessayre D, Bentata M, Degott C, et al. "Isoniazid-rifampin fulminant hepatitis." Gastroenterology 72 (1977): 284-9[3] Most JA, Markle GB "A nearly fatal hepatotoxic reaction to rifampin after halothane anesthesia." Am J Surg 127 (1974): 593[4] Bartelink AK, Lenders JW, van Herwaarden CL, et al "Fatal hepatitis after treatment with isoniazid and rifampicin in a patient on anticonvulsant therapy." Tubercle 64 (1983): 125-8[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O