INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (161296, 'Hemin', 'Rifampicin', 'Major', 'Coadministration with drugs that are capable of increasing the activity of delta-aminolevulinic acid synthetase (e.g., estrogens, barbiturates, steroid metabolites, rifampin) may negate the pharmacologic effect of hemin. In the treatment of porphyria, hemin is thought to limit the rate of porphyrin/heme biosynthesis by inhibiting delta-aminolevulinic acid synthetase, thus any induction of this enzyme would be expected to counteract the therapeutic effect of hemin.', NULL, 'Drugs capable of increasing the activity of delta-aminolevulinic acid synthetase should be avoided during hemin therapy.', NULL, 'Drugs capable of increasing the activity of delta-aminolevulinic acid synthetase should be avoided during hemin therapy.', 'Antagonism', 'Capreomycin, Pyrazinamide, Rifamycin, Bedaquiline, Sulfamethoxazole, Rifapentine, Streptomycin, Trimethoprim, Cycloserine, Rifabutin, Isoniazid, More', 'Conestat alfa, Betibeglogene autotemcel, Hyaluronidase, Streptokinase, Human C1-esterase inhibitor, Crizanlizumab, Lanadelumab, Icatibant', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/161469/', '[1] "Product Information. Panhematin (hemin)." Recordati Rare Diseases Inc, Lebanon, NJ.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (161297, 'Hydroxychloroquine', 'Rifampicin', 'Moderate', 'Coadministration with strong inducers of CYP450 2C8 or CYP450 3A4 may decrease the plasma concentrations and therapeutic effects of hydroxychloroquine. When rifampin, a moderate CYP450 2C8 and potent CYP450 3A4 inducer, was administered concomitantly with hydroxychloroquine, a lack of efficacy of hydroxychloroquine was reported. Because hydroxychloroquine and chloroquine and have similar structures and metabolic elimination pathways, a similar interaction may be observed with chloroquine.', NULL, 'Caution and close monitoring is recommended whenever hydroxychloroquine or chloroquine is used concomitantly with a potent CYP450 3A4 or 2C8 inducer.', NULL, 'Caution and close monitoring is recommended whenever hydroxychloroquine or chloroquine is used concomitantly with a potent CYP450 3A4 or 2C8 inducer. Clinical and laboratory monitoring should be considered whenever a potent CYP450 3A4 or 2C8 inducer is added to or withdrawn from therapy with hydroxychloroquine or chloroquine, and the dosage adjusted as necessary.', 'Metabolism', 'Capreomycin, Pyrazinamide, Rifamycin, Streptomycin, Trimethoprim, Cycloserine, Pyridoxine, Pretomanid, Aminosalicylic acid', 'Lumefantrine, Pyrimethamine, Tafenoquine, Halofantrine, Primaquine, Proguanil, Sulfadoxine', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/161470/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Patel S, Robinson R, Burk M "Hypertensive crisis associated with St. John''s Wort." Am J Med 112 (2002): 507-8', 1767369485);
