INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (161331, 'Rifampicin', 'Relugolix', 'Major', 'Coadministration with potent inducers of CYP450 3A4 that also induce P-glycoprotein (P-gp) may significantly decrease the plasma concentrations of relugolix. In vitro, relugolix is metabolized primarily by CYP450 3A and, to a lesser extent, by CYP450 2C8. Relugolix is also a substrate for intestinal P-gp.', NULL, 'Concomitant use of relugolix with combined P-gp and potent CYP450 3A inducers should generally be avoided.', NULL, 'Concomitant use of relugolix with combined P-gp and potent CYP450 3A inducers should generally be avoided. If coadministration is required, the manufacturer recommends increasing the relugolix dosage to 240 mg once daily. Therapeutic response and tolerance should be monitored more frequently. Following discontinuation of the P-gp/CYP450 3A inducer, treatment with relugolix should be resumed at the normally recommended dosage of 120 mg once daily.', 'Metabolism', 'Capreomycin, Pyrazinamide, Rifamycin, Streptomycin, Trimethoprim, Cycloserine, Isoniazid, Ethambutol, Pyridoxine, Ethionamide, Pretomanid, Aminosalicylic acid', 'Nafarelin, Octreotide, Cetrorelix, Pasireotide, Ganirelix, Lanreotide, Gonadorelin, Anastrozole, Aminoglutethimide, Letrozole, Fulvestrant, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/161504/', '[1] "Product Information. Orgovyx (relugolix)." Myovant Sciences, Inc., Brisbane, CA.[2] Patel S, Robinson R, Burk M "Hypertensive crisis associated with St. John''s Wort." Am J Med 112 (2002): 507-8', 1767369485);
