INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (161347, 'Selexipag', 'Rifampicin', 'Moderate', 'Coadministration with strong inducers of CYP450 2C8 and/or uridine diphosphate glucuronosyltransferase (UGT) 1A3 and/or 2B7 enzymes may reduce the plasma concentrations of selexipag and its active metabolite. However, data evaluating the interaction are not available.', NULL, 'Until further data are available, caution and clinical monitoring are recommended if selexipag is used with strong CYP450 2C8, UGT 1A3, and/or UGT 2B7 inducers, such as rifampin and rifapentine.', NULL, 'Until further data are available, caution and clinical monitoring are recommended if selexipag is used with strong CYP450 2C8, UGT 1A3, and/or UGT 2B7 inducers, such as rifampin and rifapentine.', 'Metabolism', 'Capreomycin, Pyrazinamide, Rifamycin, Bedaquiline, Sulfamethoxazole, Streptomycin, Cycloserine, Rifabutin, Isoniazid, Ethambutol, Ethionamide, More', 'Iloprost, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, Dipyridamole, Caplacizumab, Argatroban, Prasugrel, Desirudin, Eptifibatide, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/161520/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Uptravi (selexipag)." Actelion Pharmaceuticals US Inc, South San Francisco, CA.', 1767369485);
