INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (161375, 'Timolol', 'Rifampicin', 'Moderate', 'Rifampin may decrease serum levels and effects of some oral beta-blockers. The proposed mechanism is increased CYP450 oxidative metabolism of beta-blockers that are hepatically metabolized. Renally excreted beta-blockers such as atenolol, carteolol, nadolol, or sotalol are not expected to interact.', NULL, 'Patients should be monitored for clinical response and tolerance when rifampin is added to or discontinued from a beta-blocker regimen.', NULL, 'Patients should be monitored for clinical response and tolerance when rifampin is added to or discontinued from a beta-blocker regimen. The beta-blocker dosage may require adjustment.', 'Metabolism', 'Nadolol, Esmolol, Betaxolol, Nebivolol, Carteolol, Sotalol, Pindolol, Latanoprost, Apraclonidine, Methazolamide, Neostigmine, More', 'Capreomycin, Pyrazinamide, Rifamycin, Bedaquiline, Sulfamethoxazole, Rifapentine, Streptomycin, Trimethoprim, Cycloserine, Rifabutin, Isoniazid, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/161548/', '[1] Kirch W, Rose I, Klingmann I, Pabst J, Ohnhaus EE "Interaction of bisoprolol with cimetidine and rifampicin." Eur J Clin Pharmacol 31 (1986): 59-62[2] Le Coz F, Sauleman P, Poirier JM, Cuche JL, Midavaine M, Rames A, Lecocq B, Jaillon P "Oral pharmacokinetics of bisoprolol in resting and exercising healthy volunteers." J Cardiovasc Pharmacol 18 (1991): 28-34[3] "Product Information. Tenormin (atenolol)." ICN Pharmaceuticals Inc, Cost Mesa, CA.[4] Amemiya M, Tabei K, Furuya H, Sakairi Y, Asano Y "Pharmacokinetics of carteolol in patients with impaired renal function." Eur J Clin Pha