INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (161388, 'Troleandomycin', 'Rifampicin', 'Moderate', 'The coadministration of clarithromycin and rifabutin at normally recommended dosages has been reported to have resulted in significantly altered pharmacokinetics for both drugs. This bidirectional interaction is consistent with rifabutin''s cumulative inducing effect over time on the CYP450 enzymatic pathway as well as clarithromycin''s immediate inhibiting effect on the pathway. Other macrolide antibiotics may interact in a similar manner with rifamycins.', NULL, 'Some experts recommend that this combination be avoided since it may result in decreased efficacy of the macrolide and increased rifamycin toxicity (e.', NULL, 'Some experts recommend that this combination be avoided since it may result in decreased efficacy of the macrolide and increased rifamycin toxicity (e.g, neutropenia, uveitis).', 'Metabolism', 'Capreomycin, Pyrazinamide, Rifamycin, Sulfamethoxazole, Streptomycin, Trimethoprim, Cycloserine, Isoniazid, Ethambutol, Pyridoxine, Ethionamide, More', 'Lincomycin, Dirithromycin', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/161561/', '[1] Wallace RJ, Brown BA, Griffith DE, Girard W, Tanaka K "Reduced serum levels of clarithromycin in patients treated with multidrug regimens including rifampin or rifabutin for Mycobacterium avium -M.intracellulare infection." J Infect Dis 171 (1995): 747-50[2] Griffith DE, Brown BA, Girard WM, Wallace RJ Jr "Adverse events associated with high-dose rifabutin in macrolide-containing regimens for the treatment of Mycobacterium avium complex lung disease." Clin Infect Dis 21 (1995): 594-8[3] Apseloff G, Foulds G, LaBoy-Goral L, Willavize S, Vincent J "Comparison of azithromycin and clarithromycin in their interactions with rifabutin in healthy volunteers." J Clin Pharmacol 38 (1998): 830-5[4] von Rosenstiel NA, Adam D "Macrolide antibacterials. Drug interactions of clinical significance." Drug Saf 13 (1995): 105-22[5] Hafner R, Bethel J, Power M, et al. "Tolerance and pharmacokinetic interactions of rifabutin and clarithromycin in human immunodeficiency virus-infected volunteers." Antimicrob Agents Chemother 42 (1998): 631-9[6] Benson CA, Williams PL, Cohn DL, Becker S, Hojczyk P, Nevin T, Korvick JA, Heifets L, Child CC, Lederman MM, Reichman RC, "Clarithromycin or rifabutin alone or in combination for primary prophylaxis of Mycobacterium avium complex disease in patients with AIDS: A randomized, double-blind, placebo-controlled trial." J Infec Dis 181 (2000): 1289-97[7] Jordan MK, Polis MA, Kelly G, Narang PK, Masur H, Piscitelli SC "Effects of fluconazole and clarithromycin on rifabutin and 25-O-desacetylrifabutin pharmacokinetics." Antimicrob Agents Chemother 44 (2000): 2170-72[8] Benson CA, Williams PL, Currier JS, et al. "A Prospective, Randomized Trial Examining the Efficacy and Safety of Clarithromycin in Combination with Ethambutol, Rifabutin, or Both for the Treatment of Disseminated Mycobacterium avium Complex Disease in Persons with Acquired Immunodeficiency Syndrome" Clin Infect Dis 37 (2003): 1234-43', 1767369485);
