INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (161442, 'Etoposide', 'Rifapentine', 'Moderate', 'Coadministration with rifapentine may decrease the plasma concentrations of drugs that are substrates of the CYP450 2C8, 2C9, and/or 3A4 isoenzymes. The mechanism is accelerated clearance due to induction of these isoenzymes by rifapentine. Enzyme activities may be induced within 4 days of the first dose and return to normal 14 days after discontinuation of rifapentine.', NULL, 'The possibility of a diminished therapeutic response to drugs that are known substrates of CYP450 2C8, 2C9, and/or 3A4 should be considered during coadministration with rifapentine.', NULL, 'The possibility of a diminished therapeutic response to drugs that are known substrates of CYP450 2C8, 2C9, and/or 3A4 should be considered during coadministration with rifapentine. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs, particularly those with a narrow therapeutic range, whenever rifapentine is added to or withdrawn from therapy.', 'Metabolism', 'Topotecan', 'Capreomycin, Pyrazinamide, Rifamycin, Bedaquiline, Sulfamethoxazole, Streptomycin, Rifampicin, Cycloserine, Rifabutin, Pyridoxine, Pretomanid, Aminosalicylic acid', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/161615/', '[1] Thijssen HH, Flin ois JP, Beaune PH "Cytochrome P4502C9 is the principal catalyst of racemic acenocoumarol hydroxylation reactions in human liver microsomes." Drug Metab Dispos 28 (2000): 1284-90[2] "Product Information. Priftin (rifapentine)." Hoechst Marion-Roussel Inc, Kansas City, MO.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (161443, 'Etravirine', 'Rifapentine', 'Major', 'Coadministration with potent inducers of CYP450 isoenzymes may significantly decrease the plasma concentrations of etravirine, which is a substrate of CYP450 2C19, 2C9, and 3A4.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, etravirine should not be used with potent CYP450 inducers.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, etravirine should not be used with potent CYP450 inducers.', 'Metabolism', 'Capreomycin, Pyrazinamide, Rifamycin, Streptomycin, Trimethoprim, Cycloserine, Pyridoxine, Aminosalicylic acid', 'Lamivudine, Paritaprevir, Zanamivir, Valaciclovir, Velpatasvir, Oseltamivir, Acyclovir, Brincidofovir, Stavudine, Elbasvir, Famciclovir, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/161616/', '[1] "Product Information. Intelence (etravirine)." Ortho Biotech Inc, Bridgewater, NJ.[2] Patel S, Robinson R, Burk M "Hypertensive crisis associated with St. John''s Wort." Am J Med 112 (2002): 507-8[3] "Product Information. Intelence (etravirine)." Ortho Biotech Inc, Bridgewater, NJ.', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (161444, 'Everolimus', 'Rifapentine', 'Major', 'Coadministration with potent inducers of CYP450 3A4 and/or P-glycoprotein (P-gp) may significantly decrease the blood concentrations and pharmacologic effects of everolimus.', NULL, 'Concomitant use of everolimus with potent CYP450 3A4 and/or P-gp inducers should generally be avoided.', NULL, 'Concomitant use of everolimus with potent CYP450 3A4 and/or P-gp inducers should generally be avoided. Alternative therapeutic agents with less enzyme induction potential should be considered. Some manufacturers recommend that, if concomitant use is unavoidable, the daily everolimus dose should be doubled to achieve the recommended therapeutic range for the condition being treated. Please refer to the manufacturer''s labeling for specific dosing information. If the potent CYP450 3A4 and/or P-gp inducer is discontinued, the everolimus dosage should be returned to the dosage used before the potent inducer was commenced after a washout period of approximately 3 to 5 days. Everolimus whole blood trough levels should be closely monitored during treatment, particularly 2 weeks after a dose increase, and 2 weeks after discontinuation of the potent inducers.', 'Metabolism', 'Pyrazinamide, Rifamycin, Bedaquiline, Sulfamethoxazole, Trimethoprim, Cycloserine, Ethambutol, Pyridoxine, Ethionamide, Pretomanid, Aminosalicylic acid', 'Alectinib, Lenvatinib, Trametinib, Dacomitinib, Golimumab, Ustekinumab, Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Canakinumab, Anifrolumab, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/161617/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[4] "Product Information. Afinitor (everolimus)." Novartis Pharmaceuticals, East Hanover, NJ.[5] "Product Information. Zortress (everolimus)." Novartis Pharmaceuticals, East Hanover, NJ.[6] "Product Information. Afinitor (everolimus)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767369485);
