INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (161491, 'Rifapentine', 'Quinidine', 'Moderate', 'Rifamycins may increase the hepatic metabolism of quinidine by inducing CYP450 3A4 isoenzymes. Serum quinidine levels and quinidine effect may be decreased.', NULL, 'Frequent monitoring of quinidine levels and arrhythmia control is recommended.', NULL, 'Frequent monitoring of quinidine levels and arrhythmia control is recommended. Increased quinidine dosage may be needed. When a rifamycin is discontinued, enzyme induction and increased hepatic metabolism of quinidine may persist for one week. Patients should be advised to notify their physician if they experience an increase in irregular heartbeats.', 'Metabolism', 'Tocainide, Procainamide, Flecainide, Mexiletine, Bretylium, Moricizine, Ibutilide', 'Capreomycin, Pyrazinamide, Rifamycin, Streptomycin, Cycloserine, Isoniazid, Ethambutol, Pyridoxine, Ethionamide, Pretomanid, Aminosalicylic acid', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/161664/', '[1] Strayhorn VA, Baciewicz AM, Self TH "Update on rifampin drug interactions, III." Arch Intern Med 157 (1997): 2453-8[2] Reed R, Schwartz H "Phenytoin-theophylline-quinidine interactions." N Engl J Med 308 (1983): 724-5[3] Venkatesan K "Pharmacokinetic drug interactions with rifampicin." Clin Pharmacokinet 22 (1992): 47-65[4] Schwartz A, Brown JR "Quinidine-rifampin interaction." Am Heart J 107 (1984): 789-90[5] Bussey HI, Merritt GJ, Hill EG "The influence of rifampin on quinidine and digoxin." Arch Intern Med 144 (1984): 1021-3[6] Twum-Barima Y, Carruthers SG "Quinidine-rifampin interaction." N Engl J Med 304 (1981): 1466-9[7] Ahmad D, Mathur P, Ahuja S, Henderson R, Carruthers G "Rifampicin-quinidine interaction." Br J Dis Chest 73 (1979): 409-11[8] Borcherding SM, Baciewicz AM, Self TH "Update on rifampin drug interactions." Arch Intern Med 152 (1992): 711-6[9] Ace LN, Jaffe JM, Kunka RL "Effect of food and an antacid on quinidine bioavailability." Biopharm Drug Dispos 4 (1983): 183-90[10] Min DI, Ku YM, Geraets DR, Lee HC "Effect of grapefruit juice on the pharmacokinetics and pharmacodynamics of quinidine in healthy volunteers." J Clin Pharmacol 36 (1996): 469-76[11] Bailey DG, Dresser GR, Kreeft JH, Munoz C, Freeman DJ, Bend JR "Grapefruit-felodipine interaction: Effect of unprocessed fruit and probable active ingredients." Clin Pharmacol Ther 68 (2000): 468-77[12] Ha HR, Chen J, Leuenberger PM, Freiburghaus AU, Follah F "In vitro inhibition of midazolam and quinidine metabolism by flavonoids." Eur J Clin Pharmacol 48 (1995): 367-71', 1767369485);
INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (161492, 'Rifapentine', 'Relugolix', 'Major', 'Coadministration with potent inducers of CYP450 3A4 that also induce P-glycoprotein (P-gp) may significantly decrease the plasma concentrations of relugolix. In vitro, relugolix is metabolized primarily by CYP450 3A and, to a lesser extent, by CYP450 2C8. Relugolix is also a substrate for intestinal P-gp.', NULL, 'Concomitant use of relugolix with combined P-gp and potent CYP450 3A inducers should generally be avoided.', NULL, 'Concomitant use of relugolix with combined P-gp and potent CYP450 3A inducers should generally be avoided. If coadministration is required, the manufacturer recommends increasing the relugolix dosage to 240 mg once daily. Therapeutic response and tolerance should be monitored more frequently. Following discontinuation of the P-gp/CYP450 3A inducer, treatment with relugolix should be resumed at the normally recommended dosage of 120 mg once daily.', 'Metabolism', 'Capreomycin, Pyrazinamide, Rifamycin, Streptomycin, Trimethoprim, Cycloserine, Isoniazid, Ethambutol, Pyridoxine, Ethionamide, Pretomanid, Aminosalicylic acid', 'Nafarelin, Octreotide, Cetrorelix, Pasireotide, Ganirelix, Lanreotide, Gonadorelin, Anastrozole, Aminoglutethimide, Fulvestrant, Abarelix, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/161665/', '[1] "Product Information. Orgovyx (relugolix)." Myovant Sciences, Inc., Brisbane, CA.[2] Patel S, Robinson R, Burk M "Hypertensive crisis associated with St. John''s Wort." Am J Med 112 (2002): 507-8', 1767369485);
