INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (161692, 'Tocilizumab', 'Rilonacept', 'Major', 'Coadministration of interleukin-6 (IL-6) inhibitors with other biologic disease-modifying antirheumatic drugs (DMARDs) may result in increased immunosuppression and a greater risk of infection. Treatment with IL-6 inhibitors has been associated with serious, potentially life-threatening and fatal infections, including tuberculosis, invasive fungal infections such as candidiasis, aspergillosis and pneumocystis, and other opportunistic infections.', NULL, 'Concomitant use of IL-6 inhibitors with other biologic DMARDs should be avoided.', NULL, 'Concomitant use of IL-6 inhibitors with other biologic DMARDs should be avoided.', 'Others', 'Rilonacept, Apremilast, Pirfenidone, Belumosudil, Teprotumumab, Emapalumab, Ravulizumab, Pegcetacoplan, Eculizumab, Sutimlimab', 'Antithymocyte immunoglobulin (rabbit), Tocilizumab, Secukinumab, Ravulizumab, Mycophenolic acid, Apremilast, Inebilizumab, Belumosudil, Belimumab, Emapalumab, Sutimlimab, More', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/161865/', '[1] "Product Information. Simponi (golimumab)." Centocor Inc, Malvern, PA.[2] "Product Information. Kevzara (sarilumab)." sanofi-aventis, Bridgewater, NJ.[3] "Product Information. Remicade (infliximab)." Centocor Inc, Malvern, PA.[4] "Product Information. Humira (adalimumab)." Abbott Pharmaceutical, Abbott Park, IL.[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[6] "Product Information. Enbrel (etanercept)." Wyeth-Ayerst Laboratories, Philadelphia, PA.[7] Cerner Multum, Inc. "Australian Product Information." O 0[8] "Product Information. Actemra (tocilizumab