INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (161880, 'Dicoumarol', 'Rivaroxaban', 'Major', 'Concomitant use of rivaroxaban with other anticoagulants may increase the risk of bleeding. In patients receiving neuraxial anesthesia or spinal puncture, the risk of developing an epidural or spinal hematoma during rivaroxaban therapy may also be increased by the concomitant use of other drugs that affect coagulation. The development of epidural and spinal hematoma can lead to long-term neurological injury or permanent paralysis. The safety of long-term coadministration of these drugs has not been studied.', NULL, 'Due to increased bleeding risk, the use of rivaroxaban with other anticoagulants should generally be avoided except during therapeutic transition periods where patients should be observed closely.', NULL, 'Due to increased bleeding risk, the use of rivaroxaban with other anticoagulants should generally be avoided except during therapeutic transition periods where patients should be observed closely. Patients should be routinely evaluated for signs and symptoms suggesting blood loss such as a drop in hemoglobin and/or hematocrit, hypotension, or fetal distress (in pregnant women). In patients undergoing neuraxial intervention, the use of rivaroxaban should be approached with caution and only after thorough assessment of risks and benefits. Besides bleeding complications, patients should also be monitored frequently for signs and symptoms of neurologic impairment such as midline back pain, sensory and motor deficits (numbness or weakness in lower limbs), and bowel or bladder dysfunction.', 'Synergism', 'Acenocoumarol, Selexipag, Rivaroxaban', 'Warfarin, Cilostazol, Dicoumarol, Antithrombin III human, Acenocoumarol, Selexipag, Anistreplase', 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/162053/', '[1] "Product Information. Xarelto (rivaroxaban)." Bayer Inc, Toronto, IA.', 1767369485);
