INSERT OR REPLACE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (161960, 'Sorafenib', 'Rivaroxaban', 'Moderate', 'Based on in vitro inhibition data, coadministration with sorafenib may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein (P-gp) transporter. The proposed mechanism is decreased clearance in the intestine, liver, and/or kidney due to inhibition of P-glycoprotein-mediated drug efflux by sorafenib.', NULL, 'Caution is advised when sorafenib is used concurrently with drugs that are known P-gp substrates, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when sorafenib is used concurrently with drugs that are known P-gp substrates, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever sorafenib is added to or withdrawn from therapy.', 'Others', 'Dacomitinib, Bosutinib, Vandetanib, Cobimetinib, Afatinib, Axitinib, Ripretinib, Lenvatinib, Erlotinib, Osimertinib, Infigratinib, More', 'Iloprost, Lepirudin, Acenocoumarol, Urokinase, Cangrelor, Dipyridamole, Caplacizumab, Argatroban, Prasugrel, Desirudin, Eptifibatide, More', 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/162133/', '[1] "Product Information. Nexavar (sorafenib)." Bayer Pharmaceutical Inc, West Haven, CT.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Nexavar (sorafenib)." Bayer Pharmaceutical Inc, West Haven, CT.', 1767369485);
