INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1372388, 'Iobenguane (I-131)', 'Timolol (ophthalmic)', 'Major', 'Coadministration with drugs that reduce catecholamine uptake or deplete catecholamine stores may interfere with iobenguane I-131 uptake into neuroendocrine tumors such as pheochromocytoma and paraganglioma that express high levels of norepinephrine transporter on their cell surfaces. Since iobenguane is similar in structure to norepinephrine and is subject to the same uptake and accumulation pathways as norepinephrine, drugs that alter norepinephrine disposition in adrenergic nerve terminals and presynaptic storage vesicles will likewise affect iobenguane.', 'DDInter', 1767317475, 'Drugs that reduce catecholamine uptake or deplete catecholamine stores should be discontinued for at least five biological half-lives before administration of either the dosimetry dose or a therapeutic dose of iobenguane I-131. Do not administer these drugs until at least 7 days after each iobenguane I-131 dose. Patients should be monitored for the occurrence of clinically significant withdrawal symptoms, especially patients with elevated levels of circulating catecholamines and their metabolites.', 'Distribution', 'Drugs that reduce catecholamine uptake or deplete catecholamine stores should be discontinued for at least five biological half-lives before administration of either the dosimetry dose or a therapeutic dose of iobenguane I-131.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1372389, 'Iodipamide', 'Timolol (ophthalmic)', 'Moderate', 'Limited data suggest that patients receiving beta-blockers may have an increased risk of severe hypotensive and/or hypersensitivity reactions to parenteral iodinated contrast media. In addition, allergic/anaphylactoid reactions in these patients may be more difficult to treat.', 'DDInter', 1767317475, 'Patients who have received beta-blockers should be closely monitored for adverse reactions to iodinated contrast media. If anaphylaxis occurs, clinicians should be aware that beta-blockers may attenuate the response to epinephrine. Thus, larger doses of epinephrine may be necessary to overcome the bronchospasm, although such large doses can also cause excessive alpha-adrenergic stimulation resulting in hypertension, reflex bradycardia, heart block, and possible potentiation of bronchospasm. Alternative treatments recommended include vigorous supportive care (e.g., fluids) and the use of parenteral beta-agonists for bronchospasm and norepinephrine for hypotension.', 'Synergism', 'Patients who have received beta-blockers should be closely monitored for adverse reactions to iodinated contrast media.', NULL, NULL, 'Moderate', 'pharmacodynamic');
